export class SpriteManager {
    private spriteCache = new Map<string, Sprite>();

    public async loadSprite(animationData: SpriteAnimation): Promise<Sprite> {
        if (this.spriteCache.has(animationData.imagePath)) {
            const cachedSprite = this.spriteCache.get(animationData.imagePath);
            if (cachedSprite !== undefined) {
                return cachedSprite;
            }
        }

        const sprite = new Sprite(animationData.imagePath, animationData.frameCount, animationData.frameWidth, animationData.frameHeight, animationData.framePositions);
        await this.waitForManagerImageLoad(sprite.image);

        this.spriteCache.set(animationData.imagePath, sprite);
        return sprite;
    }

    private waitForManagerImageLoad(image: HTMLImageElement): Promise<void> {
        return new Promise((resolve, reject) => {
            const onLoad = () => {
                // Cleanup: Remove both event listeners
                image.removeEventListener('load', onLoad);
                image.removeEventListener('error', onError);
                resolve();
            };

            const onError = () => {
                // Cleanup: Remove both event listeners
                image.removeEventListener('load', onLoad);
                image.removeEventListener('error', onError);
                logger.error('Image failed to load: ' + image.src);
                reject(new Error(`Image failed to load: ${image.src}`)); // Reject the promise on error
            };

            // Attach the event listeners
            image.addEventListener('load', onLoad);
            image.addEventListener('error', onError);
        });
    }
}

import { useCallback } from 'react';
import axios, { type AxiosError } from 'axios';

import ENDPOINTS from '../shared/endpoints.json';
import { type IAuthProps } from './useAuth';

interface APIResponse<T> {
    status: number;
    data?: T;
    error?: string;
}

interface FileContentResponse {
    content: string;
    encoding?: string;
    sha?: string;
}

interface TreeItemResponse {
    path: string;
    type: string;
    sha?: string;
    size?: number;
}

interface RepoResponse {
    name: string;
    description?: string;
    private: boolean;
    default_branch: string;
}

interface APIError {
    status: number;
    message: string;
}

export function useAPI(auth: IAuthProps): {
    getRepoTree: (repo: string, path?: string, sha?: string) => Promise<APIResponse<TreeItemResponse[]>>,
    getFileContent: (repo: string, path: string) => Promise<APIResponse<FileContentResponse>>,
    listRecentRepos: () => Promise<APIResponse<RepoResponse[]>>
} {
    const makeAuthenticatedRequest = useCallback(async <T,>(
        endpoint: string,
        params?: Record<string, string>,
        method: 'GET' | 'POST' = 'GET'
    ): Promise<APIResponse<T>> => {
        try {
            const authResponse = await auth.validateAndRefreshToken();
            if (authResponse == null || authResponse.status !== 200 || authResponse.data == null) {
                return {
                    status: 401,
                    error: 'Authentication failed'
                };
            }

            const response = await axios({
                method,
                url: `${ENDPOINTS.api.BaseUrl}${endpoint}`,
                headers: {
                    'Authorization': `Bearer ${authResponse.data.accessToken}`,
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                params
            });

            return {
                status: response.status,
                data: response.data as T
            };
        } catch (error) {
            const apiError = error as AxiosError<APIError>;

            // Log error without using console.error
            const errorMessage = apiError.response?.data?.message ||
                                 apiError.message ||
                                 'Request failed';

            if (typeof window !== 'undefined' && window.console && window.console.error) {
                window.console.error('API request failed:', errorMessage);
            }

            return {
                status: apiError.response?.status || 500,
                error: errorMessage
            };
        }
    }, [auth.validateAndRefreshToken]);

    const getRepoTree = useCallback(async (repo: string, path?: string, sha?: string) => {
        return makeAuthenticatedRequest<TreeItemResponse[]>(ENDPOINTS.api.GetRepoTree, {
            repo,
            ...(path && { path }),
            ...(sha && { sha })
        });
    }, [makeAuthenticatedRequest]);

    const getFileContent = useCallback(async (repo: string, path: string) => {
        return makeAuthenticatedRequest<FileContentResponse>('/github/file', {
            repo,
            path
        });
    }, [makeAuthenticatedRequest]);

    const listRecentRepos = useCallback(async () => {
        return makeAuthenticatedRequest<RepoResponse[]>(ENDPOINTS.api.ListRecentRepos);
    }, [makeAuthenticatedRequest]);

    return {
        getRepoTree,
        getFileContent,
        listRecentRepos
    };
}

import { useState, useCallback, useEffect, useRef } from 'react';
import { useComputed } from '@preact/signals-react';

import { activitySignal, setNotification, bypassTutorialSignal } from 'src/signals/appSignals';
import {
    getIncompletePhrasesByTutorialGroup, initializeGame,
    setCompletedGamePhrase,
    getNextGamePhrase,
    setGamePhrase,
} from 'src/signals/gameSignals';
import {
    setNextTutorial, resetCompletedTutorials,
    tutorialSignal, getNextTutorial, setCompletedTutorial
} from 'src/signals/tutorialSignals';
import { createLogger } from 'src/utils/Logger';
import { navigate, parseLocation } from 'src/utils/navigationUtils';

import { type ActionType } from '../game/types/ActionTypes';
import { ActivityType, type ParsedCommand, type GamePhrase, type Tutorial } from '../types/Types';
import GamePhrases from '../utils/GamePhrases';

import { useTutorial } from './useTutorials';


const logger = createLogger({ prefix: 'ActivityMediator' });

export function useActivityMediator(): {
    isInGameMode: boolean;
    isInTutorial: boolean;
    isInEdit: boolean;
    isInNormal: boolean;
    checkTutorialProgress: (command: string | null) => void;
    heroAction: ActionType;
    zombie4Action: ActionType;
    handleCommandExecuted: (parsedCommand: ParsedCommand) => boolean;
    setHeroAction: React.Dispatch<React.SetStateAction<ActionType>>;
    setZombie4Action: React.Dispatch<React.SetStateAction<ActionType>>;
    checkGameProgress: (successPhrase: GamePhrase) => void;
} {
    const [heroAction, setHeroAction] = useState<ActionType>('Idle');
    const [zombie4Action, setZombie4Action] = useState<ActionType>('Walk');
    const {
        getIncompleteTutorialsInGroup,
        canUnlockTutorial
    } = useTutorial();
    const activity = useComputed(() => activitySignal.value).value;
    const bypassTutorial = useComputed(() => bypassTutorialSignal.value);
    const currentTutorialRef = useRef<Tutorial | null>(null);

    const transitionToGame = useCallback((contentKey?: string | null, groupKey?: string | null): void => {
        // First initialize game if group key is provided
        if (groupKey) {
            initializeGame(groupKey);
        } else {
            initializeGame();
        }
        // Then update activity and navigate
        activitySignal.value = ActivityType.GAME;
        navigate({
            activityKey: ActivityType.GAME,
            contentKey: contentKey ?? null,
            groupKey: groupKey ?? null
        });
    }, []);

    const decideActivityChange = useCallback((commandActivity: ActivityType | null = null): ActivityType => {
        logger.debug('Deciding activity change:', {
            commandActivity,
            currentActivity: activity,
            bypassTutorial: bypassTutorial.value,
            currentTutorial: tutorialSignal.value
        });

        const bypassTutorialValue = bypassTutorial.value;
        if (bypassTutorialValue) {
            return ActivityType.NORMAL;
        }
        if (tutorialSignal.value && !tutorialSignal.value?.tutorialGroup && activity !== ActivityType.GAME) {
            return ActivityType.TUTORIAL;
        }

        if (currentTutorialRef.current && currentTutorialRef.current?.tutorialGroup && activity !== ActivityType.GAME) {
            return ActivityType.GAME;
        }
        if (getNextTutorial()) commandActivity = ActivityType.TUTORIAL;

        return commandActivity ?? ActivityType.NORMAL;
    }, [activity, bypassTutorial.value]);

    const checkGameProgress = useCallback((successPhrase: GamePhrase) => {
        logger.debug('Checking game progress:', successPhrase);
        const groupKey = parseLocation().groupKey ?? '';
        setCompletedGamePhrase(successPhrase.key);
        if (groupKey) {
            const nextPhraseInGroup = getIncompletePhrasesByTutorialGroup(groupKey)[0];
            if (nextPhraseInGroup) {
                setGamePhrase(getNextGamePhrase());
                transitionToGame(nextPhraseInGroup.key, nextPhraseInGroup.tutorialGroup);
                return;
            }
            const incompleteTutorialInGroup = getIncompleteTutorialsInGroup(groupKey);
            incompleteTutorialInGroup.forEach(itig => {
                setCompletedTutorial(itig.phrase);
            });
            const nextTutorial = getNextTutorial();
            if (nextTutorial) {
                const resultActivity = decideActivityChange(ActivityType.TUTORIAL);
                navigate({
                    activityKey: resultActivity,
                    contentKey: nextTutorial.phrase ?? '',
                    groupKey: nextTutorial.tutorialGroup ?? ''
                })
                return;
            }
        }

        const nextGamePhrase = getNextGamePhrase();
        if (nextGamePhrase) {
            setGamePhrase(nextGamePhrase);
            transitionToGame(nextGamePhrase.key, nextGamePhrase.tutorialGroup);
            return;
        }
        activitySignal.value = ActivityType.NORMAL;
        navigate({ activityKey: ActivityType.NORMAL })
    }, [getIncompleteTutorialsInGroup, transitionToGame]);

    const checkTutorialProgress = useCallback((command: string | null) => {
        logger.debug('Checking tutorial progress:', {
            command,
            currentTutorial: tutorialSignal.value,
            activity: activitySignal.value,
            location: parseLocation()
        });

        currentTutorialRef.current = getNextTutorial();
        if (!currentTutorialRef.current) {
            logger.debug('No current tutorial found');
            return;
        }

        const groupKey = parseLocation().groupKey ?? '';
        // Normalize command for Enter key
        command = command === '' ? '\r' : command;
        if (command) {
            logger.debug('Checking if can unlock tutorial:', {
                command,
                currentPhrase: currentTutorialRef.current.phrase,
                charCodesCommand: [...command].map(c => c.charCodeAt(0)),
                charCodesPhrase: [...currentTutorialRef.current.phrase].map(c => c.charCodeAt(0))
            });

            if (canUnlockTutorial(command)) {
                logger.debug('Tutorial unlocked:', command);
                if (groupKey) {
                    const incompletePhrasesInGroup = getIncompletePhrasesByTutorialGroup(groupKey)[0];
                    if (incompletePhrasesInGroup) {
                        transitionToGame(incompletePhrasesInGroup.key, incompletePhrasesInGroup.tutorialGroup);
                    }
                    return;
                }
                setCompletedTutorial(currentTutorialRef.current.phrase);
            } else {
                logger.debug('Tutorial not unlocked:', {
                    expected: currentTutorialRef.current.phrase,
                    received: command
                });
                setNotification(
                    `Tutorial ${tutorialSignal.value?.phrase} not unlocked with ${command}`
                )
                return;
            }
        }

        const nextTutorial = getNextTutorial();
        logger.debug('Next tutorial:', nextTutorial);
        if (nextTutorial?.phrase) {
            const resultActivity = decideActivityChange(ActivityType.TUTORIAL);
            setNextTutorial(nextTutorial);
            navigate({
                activityKey: resultActivity,
                contentKey: nextTutorial.phrase,
                groupKey: nextTutorial.tutorialGroup ?? null
            })
            return;
        }
        const nextGamePhrase = getNextGamePhrase();
        if (nextGamePhrase) {
            transitionToGame(nextGamePhrase?.key, groupKey);
        }
        return;
    }, [canUnlockTutorial, transitionToGame]);

    const handleCommandExecuted = useCallback((parsedCommand: ParsedCommand): boolean => {
        logger.debug('Handling command:', parsedCommand);
        let result = false;
        if (parseLocation().activityKey === ActivityType.TUTORIAL) {
            checkTutorialProgress(parsedCommand.command);
        }
        else if (parseLocation().activityKey === ActivityType.GAME && parseLocation().contentKey) {
            const gamePhrase = GamePhrases.getGamePhraseByKey(parseLocation().contentKey ?? '')
            if (gamePhrase) checkGameProgress(gamePhrase);
        }
        switch (parsedCommand.command) {
            case 'play': {
                const nextGamePhrase = getNextGamePhrase();
                transitionToGame(nextGamePhrase?.key);
                result = true;
                break;
            }
            case 'tut': {
                if ('r' in parsedCommand.switches) {
                    resetCompletedTutorials();
                }
                const nextTutorial = getNextTutorial();
                navigate({
                    activityKey: ActivityType.TUTORIAL,
                    contentKey: nextTutorial?.phrase ?? null,
                    groupKey: nextTutorial?.tutorialGroup ?? null
                })
                result = true;
                break;
            }
            default:
                result = false;
        }

        return result;
    }, [checkGameProgress, checkTutorialProgress, transitionToGame]);

    useEffect(() => {
        const resultActivity = decideActivityChange(null);
        if (resultActivity === ActivityType.TUTORIAL) {
            checkTutorialProgress(null);
        }
    }, [decideActivityChange, checkTutorialProgress]);

    return {
        isInGameMode: activity === ActivityType.GAME,
        isInTutorial: activity === ActivityType.TUTORIAL,
        isInEdit: activity === ActivityType.EDIT,
        isInNormal: activity === ActivityType.NORMAL,
        checkTutorialProgress,
        heroAction,
        zombie4Action,
        handleCommandExecuted,
        setHeroAction,
        setZombie4Action,
        checkGameProgress,
    };
}

// src/hooks/useCommand.ts
import { useComputed } from '@preact/signals-react';
import type React from 'react';
import { useContext, useState, useCallback, useEffect } from 'react';

import { activitySignal, appendToOutput } from 'src/signals/appSignals';
import { setCommandTime } from 'src/signals/commandLineSignals';
import { createLogger } from 'src/utils/Logger';

import { commandRegistry } from '../commands/commandRegistry';
import { CommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { LogKeys } from '../types/TerminalTypes';
import { ActivityType, type OutputElement, type ParsedCommand } from '../types/Types';
import { parsedCommandToString, loadCommandHistory, saveCommandHistory } from '../utils/commandUtils';

import { useActivityMediator } from './useActivityMediator';
import { useWPMCalculator } from './useWPMCaculator';


const logger: ReturnType<typeof createLogger> = createLogger({ prefix: 'useCommand' });

export const useCommand = (): {
  output: OutputElement[];
  resetOutput: () => void;
  commandHistory: string[];
  addToCommandHistory: (command: ParsedCommand | string) => void;
  getCommandResponseHistory: () => string[];
  handleCommand: (parsedCommand: ParsedCommand) => Promise<void>;
  commandHistoryIndex: number;
  setCommandHistoryIndex: React.Dispatch<React.SetStateAction<number>>;
  commandHistoryFilter: string | null;
  setCommandHistoryFilter: React.Dispatch<React.SetStateAction<string | null>>;
  appendToOutput: (outputElement: OutputElement) => void;
  executeCommand: (parsedCommand: ParsedCommand) => Promise<void>;
} => {
    const [output, setOutput] = useState<OutputElement[]>([]);
    const [commandHistory, setCommandHistory] = useState<string[]>([]);
    const [commandHistoryIndex, setCommandHistoryIndex] = useState(-1);
    const [commandHistoryFilter, setCommandHistoryFilter] = useState<string | null>(null);
    const currentActivity = useComputed(() => activitySignal.value);
    const {
        handleCommandExecuted,
        checkTutorialProgress
    } = useActivityMediator();
    const wpmCalculator = useWPMCalculator();
    const context = useContext(CommandContext);

    if (context === undefined) {
        throw new Error('useCommand must be used within a CommandProvider');
    }

    // Load command history from localStorage on mount
    useEffect(() => {
        const savedHistory = loadCommandHistory();
        if (savedHistory && savedHistory.length > 0) {
            logger.debug('Loaded command history:', savedHistory);
            setCommandHistory(savedHistory);
        }
    }, []);

    const resetOutput = useCallback((): void => {
        logger.debug('Resetting output');
        setOutput([]);
    }, []);

    const addToCommandHistory = useCallback((command: ParsedCommand | string): void => {
        const commandString = typeof command === 'string'
            ? command
            : parsedCommandToString(command);

        logger.debug('Adding to command history:', commandString);
        setCommandHistory(prev => {
            // Don't add if it's identical to the previous command
            if (prev.length > 0 && prev[prev.length - 1] === commandString) {
                return prev;
            }
            const newHistory = [...prev, commandString].slice(-120); // Keep last 120 commands
            saveCommandHistory(newHistory); // Save to localStorage whenever history changes
            return newHistory;
        });
    }, []);

    const getCommandResponseHistory = useCallback((): string[] => {
        const keys: string[] = [];
        const history: string[] = [];
        for (let i = 0; i < localStorage.length; i++) {
            const key = localStorage.key(i);
            if (key?.startsWith(LogKeys.Command + '_')) {
                keys.push(key);
            }
        }
        keys.sort();
        for (const key of keys) {
            const historyJSON = localStorage.getItem(key);
            if (historyJSON) {
                history.push(historyJSON);
            }
        }
        logger.debug('Retrieved command response history:', history);
        return history;
    }, []);

    const createCommandRecord = useCallback((
        command: ParsedCommand,
        response: React.ReactNode,
        status: number,
        commandTime: Date,
        sensitive: boolean
    ): OutputElement => {
        const wpms = wpmCalculator.getWPMs();
        logger.debug('Creating command record:', { command, response, status });
        return {
            command,
            response,
            status,
            wpmAverage: wpms.wpmAverage,
            characterAverages: wpms.charWpms,
            commandTime,
            sensitive: sensitive ?? false
        };
    }, [wpmCalculator]);

    const processCommandOutput = useCallback((
        command: ParsedCommand,
        response: ICommandResponse,
    ): void => {
        logger.debug('Processing command output:', { command, response });
        const commandTime = new Date();
        const outputElement = createCommandRecord(
            command,
            response.message,
            response.status,
            commandTime,
            response.sensitive ?? false
        );
        appendToOutput(outputElement);
        addToCommandHistory(command);
    }, [createCommandRecord, addToCommandHistory]);

    const executeCommand = useCallback(async (parsedCommand: ParsedCommand) => {
        // Breakpoint 5: Start of command execution
        logger.debug('Executing command:', parsedCommand);
        const command = commandRegistry.getCommand(parsedCommand.command);
        if (command && context) {
            try {
                const response = await command.execute(context, parsedCommand);
                logger.debug('Command executed successfully:', response);
                processCommandOutput(parsedCommand, response);
                handleCommandExecuted(parsedCommand);
            } catch (error) {
                const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
                logger.error('Command execution failed:', error);
                processCommandOutput(parsedCommand, {
                    status: 500,
                    message: errorMessage
                });
                handleCommandExecuted(parsedCommand);
            }
        } else {
            // Breakpoint 6: Tutorial command handling
            const response = currentActivity.value === ActivityType.TUTORIAL ? `Tutorial attempt: ${parsedCommand.command}` : `Command not found: ${parsedCommand.command}`;
            logger.debug('Command not found:', { command: parsedCommand.command, activity: currentActivity.value });
            processCommandOutput(parsedCommand, {
                status: 404,
                message: response
            });
            handleCommandExecuted(parsedCommand);
        }
    }, [context, processCommandOutput, handleCommandExecuted, currentActivity]);

    const handleCommand = useCallback(async (parsedCommand: ParsedCommand) => {
        // Breakpoint 7: Start of command handling
        logger.debug('Handling command:', { parsedCommand, activity: currentActivity.value });
        setCommandTime(new Date());
        if (currentActivity.value === ActivityType.TUTORIAL) {
            // Breakpoint 8: Tutorial progress check
            logger.debug('Processing tutorial command:', parsedCommand.command);
            checkTutorialProgress(parsedCommand.command);
        }

        await executeCommand(parsedCommand);
    }, [currentActivity.value, executeCommand, checkTutorialProgress]);

    return {
        output,
        resetOutput,
        commandHistory,
        addToCommandHistory,
        getCommandResponseHistory,
        handleCommand,
        commandHistoryIndex,
        setCommandHistoryIndex,
        commandHistoryFilter,
        setCommandHistoryFilter,
        appendToOutput,
        executeCommand
    };
};

import { useSignal, useComputed } from '@preact/signals-react';
import { useEffect } from 'react';
import { useLocation, useSearchParams } from 'react-router-dom';

export function useReactiveLocation() {
  const location = useLocation();
  useSearchParams();

  const pathSignal = useSignal(location.pathname);
  const searchSignal = useSignal(location.search);
  const isInitialized = useSignal(false);

  useEffect(() => {
    if (!isInitialized.value) {
      pathSignal.value = location.pathname;
      searchSignal.value = location.search;
      isInitialized.value = true;
    } else {
      // Only update if the location has actually changed
      if (pathSignal.value !== location.pathname || searchSignal.value !== location.search) {
        pathSignal.value = location.pathname;
        searchSignal.value = location.search;
      }
    }
  }, [location, pathSignal, searchSignal, isInitialized]);


  return {
    pathname: pathSignal,
    search: searchSignal,
    isInitialized: useComputed(() => isInitialized.value)
  };
}

// useResizeCanvasAndFont.ts
import { useState, useCallback, type TouchEventHandler, useRef } from 'react';

export function useResizeCanvasAndFont(): {
    fontSize: number;
    canvasHeight: number;
    handleTouchMove: (event: TouchEvent) => void;
    handleTouchStart: TouchEventHandler<HTMLElement>;
    handleTouchEnd: TouchEventHandler<HTMLDivElement>;
} {
    const initialFontSize = parseInt(localStorage.getItem("fontSize") ?? '17');
    const initialCanvasHeight = parseInt(localStorage.getItem('canvas-height') ?? '100');
    const [fontSize, setFontSize] = useState(initialFontSize);
    const [canvasHeight, setCanvasHeight] = useState(initialCanvasHeight);
    const lastTouchDistanceRef = useRef<number | null>(null);

    const handleTouchMove = useCallback((event: TouchEvent) => {
        if (event.touches.length === 2) {
            event.preventDefault();

            const currentDistance =
                getDistanceBetweenTouches(event.touches);
            if (lastTouchDistanceRef.current && lastTouchDistanceRef.current > 0) {
                const eventTarget = event.target as HTMLElement;
                const scaleFactor = currentDistance /
                    lastTouchDistanceRef.current;
                if (eventTarget && eventTarget.nodeName ===
                    'CANVAS') {
                    setCanvasHeight(prevCanvasHeight => prevCanvasHeight * scaleFactor);
                } else {
                    setFontSize(prevFontSize => prevFontSize *
                        scaleFactor);
                    document.documentElement.style.setProperty('--rminal-font-size', `${fontSize}px`);
                }
                lastTouchDistanceRef.current = currentDistance;
            }
        }
    }, [fontSize]);

    const handleTouchStart: TouchEventHandler<HTMLElement> = (event: React.TouchEvent<HTMLElement>) => {
        setTimeout(() => {
            // this.terminalElement.focus();
        }, 500)
        if (event.touches.length === 2) {
            // event.preventDefault();
            lastTouchDistanceRef.current = getDistanceBetweenTouches(event.touches as unknown as TouchList);
        }
    }

    const handleTouchEnd: TouchEventHandler<HTMLDivElement> = () => {
        localStorage.setItem('terminalFontSize', `${fontSize}`);
        lastTouchDistanceRef.current = null;
    }

    const getDistanceBetweenTouches = (touches: TouchList): number => {
        const touch1 = touches[0];
        const touch2 = touches[1];
        return Math.sqrt(
            Math.pow(touch2.pageX - touch1.pageX, 2) +
            Math.pow(touch2.pageY - touch1.pageY, 2),
        );
    }

    // Return the state and handlers
    return {
        fontSize,
        canvasHeight,
        handleTouchMove,
        handleTouchStart,
        handleTouchEnd,
    };
}

import { useRef, useEffect } from 'react';

export function useStateLogger<T>(state: T, name: string): void {
  const prevStateRef = useRef<string | null>(null);

  useEffect(() => {
    try {
      const currentState = JSON.stringify(state);

      if (prevStateRef.current !== currentState) {
        const prevState = prevStateRef.current ? JSON.parse(prevStateRef.current) as T : {} as T;
        const stateChangeLog = {
          from: prevState,
          to: state
        };

        // Log error without using console.log
        const logMessage = `[${name}] State changed: ${JSON.stringify(stateChangeLog, null, 2)}`;
        if (typeof window !== 'undefined' && window.console && window.console.log) {
          window.console.log(logMessage);
        }

        prevStateRef.current = currentState;
      }
    } catch (error) {
      // Silent error handling to prevent breaking the application
      const errorMessage = error instanceof Error ? error.message : 'Unknown error in state logging';
      if (typeof window !== 'undefined' && window.console && window.console.error) {
        window.console.error(`[${name}] State logging error:`, errorMessage);
      }
    }
  }, [state, name]);
}

// hooks/useTerminal.ts
import { useCallback, useEffect, useRef, useState } from 'react';
import { useComputed } from '@preact/signals-react';
import { FitAddon } from '@xterm/addon-fit';
import { useXTerm } from 'react-xtermjs';

import { TERMINAL_CONSTANTS } from 'src/constants/terminal';
import {
  isInLoginProcessSignal,
  isInSignUpProcessSignal,
  setActivity,
  setIsInLoginProcess,
  setIsInSignUpProcess,
  setTempEmail,
  setTempPassword,
  setTempUserName,
  tempEmailSignal,
  tempPasswordSignal,
  tempUserNameSignal
} from 'src/signals/appSignals';
import { addKeystroke, commandLineSignal , setCommandLine } from 'src/signals/commandLineSignals';
import { ActivityType } from 'src/types/Types';
import { parseCommand } from 'src/utils/commandUtils';
import { createLogger } from 'src/utils/Logger';

import { XtermAdapterConfig } from '../components/XtermAdapterConfig';

import { useCharacterHandler } from './useCharacterHandler';
import { useCommand } from './useCommand';
