// gameSignals.ts
import { signal } from "@preact/signals-react";

import { type ActionType } from "src/game/types/ActionTypes";
import { type GamePhrase, Phrases } from "src/types/Types";
import { createPersistentSignal } from "src/utils/signalPersistence";

export const startGameSignal = signal<string | undefined>(undefined);
export const gamePhraseSignal = signal<GamePhrase | null>(null);
export const gameInitSignal = signal<boolean>(false);
export const isInGameModeSignal = signal<boolean>(false);
export const currentGamePhraseSignal = signal<GamePhrase | null>(null);
export const gameLevelSignal = signal<number | null>(null);
export const heroActionSignal = signal<ActionType>('Idle');
export const zombie4ActionSignal = signal<ActionType>('Walk');

const completedGamePhrasesKey = 'completed-game-phrases';

export const setHeroAction = (action: ActionType): void => {
  heroActionSignal.value = action;
};

export const setZombie4Action = (action: ActionType): void => {
  zombie4ActionSignal.value = action;
};

export const setGameLevel = (level: number): void => {
  gameLevelSignal.value = level;
};

const { signal: completedGamePhrasesSignal, update: updateCompletedGamePhrases } = createPersistentSignal<Set<string>>({
  key: completedGamePhrasesKey,
  signal: signal<Set<string>>(new Set()),
  serialize: (value: Set<string>) => JSON.stringify([...value]),
  deserialize: (value: string) => new Set(JSON.parse(value) as string[]),
});

export { completedGamePhrasesSignal };

export const setCompletedGamePhrase = (gamePhraseId: string): void => {
  updateCompletedGamePhrases(prev => prev.add(gamePhraseId));
}

export const getIncompletePhrasesByTutorialGroup = (tutorialGroup: string): GamePhrase[] => {
  const phrasesInGroup = Phrases.filter(p => p.tutorialGroup === tutorialGroup);
  const incompletePhrasesInGroup = phrasesInGroup
    .filter(pig => !Array.from(completedGamePhrasesSignal.value).includes(pig.key))
  return incompletePhrasesInGroup;
}

export const getNextGamePhrase = (): GamePhrase | null => {
  const nextGamePhrase = Phrases
    .find(t => !completedGamePhrasesSignal.value.has(t.key));
  if (nextGamePhrase === undefined) {
    return null;
  }
  return nextGamePhrase;
};

export const initializeGame = (tutorialGroup?: string): void => {
  gameInitSignal.value = true;
  isInGameModeSignal.value = true;
  if (tutorialGroup) {
    const tutorialGroupGamePhrase = getIncompletePhrasesByTutorialGroup(tutorialGroup);
    if (tutorialGroupGamePhrase.length > 0) {
      gamePhraseSignal.value = tutorialGroupGamePhrase[0] ?? null;
    }
  }
};

export const setGamePhrase = (phrase: GamePhrase | null): void => {
  gamePhraseSignal.value = phrase;
};

// Load initial state
const loadInitialState = (): void => {
  const storedTutorials = localStorage.getItem(completedGamePhrasesKey);
  if (storedTutorials) {
    try {
      const parsedTutorials = JSON.parse(storedTutorials) as string[];
      completedGamePhrasesSignal.value = new Set(parsedTutorials);
    } catch (error) {
      console.error('Failed to parse stored tutorials:', error);
      completedGamePhrasesSignal.value = new Set();
    }
  }
  setGamePhrase(getNextGamePhrase());
};

loadInitialState();

// src/signals/tutorialSignals.ts
import { computed, signal } from "@preact/signals-react";

import { type Tutorial, Tutorials } from "src/types/Types";
import { createLogger } from "src/utils/Logger";

import { createPersistentSignal } from "../utils/signalPersistence";

const logger = createLogger({ prefix: 'tutorialSignals' });

const completedTutorialsKey = "completed-tutorials";

const { signal: completedTutorialsSignal, update: updateCompletedTutorials } = createPersistentSignal({
  key: completedTutorialsKey,
  signal: signal<Set<string>>(new Set()),
  serialize: (value) => JSON.stringify([...value]),
  deserialize: (value) => new Set(JSON.parse(value) as string[]),
});

export { completedTutorialsSignal };

export const tutorialSignal = signal<Tutorial | null>(null);

/**
 * Gets the next uncompleted tutorial
 * @returns The next Tutorial or null if all are completed
 */
export const getNextTutorial = (): Tutorial | null => {
  const nextTutorial = Tutorials
    .find(t => !completedTutorialsSignal.value.has(t.phrase));
  logger.debug('Getting next tutorial:', { nextTutorial, completedTutorials: [...completedTutorialsSignal.value] });
  return nextTutorial ?? null;
};

/**
 * Sets the next tutorial
 * @param nextTutorial - The tutorial to set as next
 */
export const setNextTutorial = (nextTutorial: Tutorial | null): void => {
  logger.debug('Setting next tutorial:', nextTutorial);
  tutorialSignal.value = nextTutorial;
};

// Load initial state
const loadInitialState = () => {
  const storedTutorials = localStorage.getItem(completedTutorialsKey);
  if (storedTutorials) {
    logger.debug('Loading stored tutorials:', storedTutorials);
    completedTutorialsSignal.value = new Set(JSON.parse(storedTutorials) as string[]);
  }
  const nextTutorial = getNextTutorial();
  logger.debug('Initial tutorial:', nextTutorial);
  setNextTutorial(nextTutorial);
};

loadInitialState();

// Computed signals
export const completedTutorialsArray = computed(() => [...completedTutorialsSignal.value]);

// Exported functions
/**
 * Marks a tutorial as completed and sets the next tutorial
 * @param tutorialId - The ID of the completed tutorial
 */
export const setCompletedTutorial = (tutorialId: string): void => {
  logger.debug('Setting completed tutorial:', { tutorialId, currentTutorial: tutorialSignal.value });
  updateCompletedTutorials(prev => new Set(prev).add(tutorialId));
  // After marking a tutorial as complete, get and set the next one
  const nextTutorial = getNextTutorial();
  logger.debug('Next tutorial after completion:', nextTutorial);
  setNextTutorial(nextTutorial);
};

/**
 * Resets all completed tutorials and sets the first tutorial
 */
export const resetCompletedTutorials = (): void => {
  logger.debug('Resetting completed tutorials');
  updateCompletedTutorials(new Set());
  const nextTutorial = getNextTutorial();
  logger.debug('First tutorial after reset:', nextTutorial);
  setNextTutorial(nextTutorial);
};

import { Mock } from 'vitest';

export interface MockCanvasContext {
  fillRect: Mock;
  clearRect: Mock;
  drawImage: Mock;
  beginPath: Mock;
  closePath: Mock;
  stroke: Mock;
  fill: Mock;
}

export interface CanvasMock {
  mockGetContext: Mock;
  restoreOriginalGetContext: () => void;
}

export function createMockCanvasContext(): MockCanvasContext;
export function setupCanvasMock(): CanvasMock;

import { vi, type Mock } from 'vitest';

// Define an interface for the mock canvas context methods
interface MockCanvasContext {
  fillRect: Mock<any, any>;
  clearRect: Mock<any, any>;
  drawImage: Mock<any, any>;
  beginPath: Mock<any, any>;
  closePath: Mock<any, any>;
  stroke: Mock<any, any>;
  fill: Mock<any, any>;
}

// Create a reusable canvas context mock
export const createMockCanvasContext = (): MockCanvasContext => {
  const mockContext: MockCanvasContext = {
    fillRect: vi.fn(),
    clearRect: vi.fn(),
    drawImage: vi.fn(),
    beginPath: vi.fn(),
    closePath: vi.fn(),
    stroke: vi.fn(),
    fill: vi.fn(),
  };

  return mockContext;
};

// Global mock for canvas context
export const setupCanvasMock = (): {
  mockGetContext: Mock<any, any>;
  restoreOriginalGetContext: () => void;
} => {
  const originalGetContext = HTMLCanvasElement.prototype.getContext;
  const mockGetContext = vi.fn(() => createMockCanvasContext());

  Object.defineProperty(HTMLCanvasElement.prototype, 'getContext', {
    value: mockGetContext,
    configurable: true,
    writable: true
  });

  return {
    mockGetContext,
    restoreOriginalGetContext: () => {
      // Safely restore original implementation
      if (originalGetContext) {
        Object.defineProperty(HTMLCanvasElement.prototype, 'getContext', {
          value: originalGetContext,
          configurable: true,
          writable: true
        });
      } else {
        // If no original method existed, remove the property
        const canvasProto = HTMLCanvasElement.prototype as { getContext?: unknown };
        delete canvasProto.getContext;
      }
    }
  };
};

// Export the type for CanvasMock
export type CanvasMock = ReturnType<typeof setupCanvasMock>;

import { vi } from 'vitest';

