  (window as WindowWithXtermCallback).__xtermDataCallback?.(data);
};

import { type GamePhrase, type MyResponse, Phrases } from "../types/Types";

interface PhraseAchievement {
    wpm: string;
    phraseName: string;
}

const standardChars = /^[a-zA-Z0-9\s'";:.!,?]+$/;

export default class GamePhrases {
    /**
     * Gets a game phrase by its key
     * @param key - The phrase key to search for
     * @returns The matching GamePhrase or null if not found
     */
    public static getGamePhraseByKey(key: string): GamePhrase | null {
        const phrase = Phrases.find(x => x.key == key);
        if (phrase != null) {
            // Return specified phrase, if it exists.
            return phrase;
        }
        // Else, return first phrase.
        return this.getGamePhraseByIndex(0);
    }

    /**
     * Gets game phrases by tutorial group
     * @param tutorialGroup - The tutorial group to filter by
     * @returns Array of matching GamePhrases
     */
    public static getGamePhrasesByTutorialGroup(tutorialGroup: string): GamePhrase[] {
        const phrases = Phrases.filter(p => tutorialGroup.includes(p.tutorialGroup ?? 'exclude'))
        return phrases;
    }

    /**
     * Validates all game phrases for standard characters
     * @returns MyResponse containing validation results
     */
    public checkGamePhrases(): MyResponse<string[]> {
        const response: MyResponse<string[]> = {
            status: 200,
            message: '',
            data: [],
            error: []
        };
        Phrases.forEach((phrase, index) => {
            if (!standardChars.test(phrase.value)) {
                response.error.push(`Phrase at index ${index} contains non-standard characters: ${phrase.value}`);
                response.status = 400;
            }
        });
        return response;
    }

    /**
     * Gets a game phrase by its index
     * @param index - The index of the phrase to retrieve
     * @returns The GamePhrase at the specified index or null if invalid
     */
    public static getGamePhraseByIndex(index: number): GamePhrase | null {
        if (index >= 0 && index < Phrases.length && Phrases[index] != null) {
            return Phrases[index];
        }
        if (index < 0 && Phrases[0] != null) return Phrases[0];
        if (index >= Phrases.length && Phrases[Phrases.length - 1] != null){
             return Phrases[Phrases.length - 1] ?? null;
        }
        if (Phrases[0] != null) return Phrases[0];
        throw new Error('No phrases available');
    }

    /**
     * Gets a game phrase by its value
     * @param phrase - The phrase text to search for
     * @returns The matching GamePhrase or null if not found
     */
    public static getGamePhraseByValue(phrase: string): GamePhrase | null {
        const result = Phrases.find(p => p.value === phrase)
        return result ?? null;
    }

    /**
     * Gets all game phrase names
     * @returns Array of phrase names
     */
    public static getGamePhraseNames(): string[] {
        return Phrases.map(x => x.key);
    }

    /**
     * Gets a random game phrase
     * @returns A random phrase value
     * @throws Error if no phrases are available
     */
    public static getRandomGamePhrase(): string {
        const phrasesLength = Phrases.length;
        if (phrasesLength === 0) throw new Error('No phrases available');
        const randomKey = Math.floor(Math.random() * phrasesLength);
        const phrase = Phrases[randomKey];
        if (phrase == null) throw new Error('Invalid phrase index');
        return phrase.value;
    }

    /**
     * Gets achieved phrases from localStorage
     * @returns Array of PhraseAchievement objects
     */
    public static getGamePhrasesAchieved(): PhraseAchievement[] {
        const storedPhrasesAchieved = localStorage.getItem('phrasesAchieved');

        try {
            const parsedPhrases: string[] = JSON.parse(storedPhrasesAchieved ?? '[]') as string[];
            return parsedPhrases.map((phrase: string) => {
                const [wpm, phraseName] = phrase.split(':');
                return {
                    wpm: wpm ?? '',
                    phraseName: phraseName ?? ''
                };
            });
        } catch (error: unknown) {
            // Log error without using console.error
            const errorMessage = error instanceof Error ? error.message : 'Unknown parsing error';
            if (typeof window !== 'undefined' && window.console?.error) {
                window.console.error('Error parsing phrases achieved:', errorMessage);
            }
            return [];
        }
    }

    /**
     * Resets achieved phrases in localStorage
     */
    public static resetGamePhrasesAchieved(): void {
        localStorage.removeItem('phrasesAchieved');
    }
}

enum LogLevel {
  DEBUG = 0,
  INFO = 1,
  WARN = 2,
  ERROR = 3,
  SILENT = 4
}

interface LoggerConfig {
  level?: LogLevel;
  prefix?: string;
  logToConsole?: boolean;
  logToFile?: boolean;
}

class Logger {
  private config: LoggerConfig;

  constructor(config: Partial<LoggerConfig> = {}) {
    this.config = {
      level: config.level ?? LogLevel.INFO,
      prefix: config.prefix ?? 'HandTerm',
      logToConsole: config.logToConsole ?? true,
      logToFile: config.logToFile ?? false
    };
  }

  private log<T extends unknown[]>(level: LogLevel, message: string, ...args: T): void {
    if (this.config.level !== undefined && level < this.config.level) return;

    const timestamp = new Date().toISOString();
    const formattedMessage = `[${timestamp}] [${this.config.prefix}] [${LogLevel[level]}] ${message}`;

    if (this.config.logToConsole === true) {
      this.safeConsoleLog(level, formattedMessage, ...args);
    }

    if (this.config.logToFile === true) {
      this.writeToLogFile(formattedMessage, ...args);
    }
  }

  private safeConsoleLog<T extends unknown[]>(level: LogLevel, formattedMessage: string, ...args: T): void {
    if (typeof window !== 'undefined' && window.console) {
      switch (level) {
        case LogLevel.DEBUG:
          window.console.log(formattedMessage, ...args);
          break;
        case LogLevel.INFO:
          window.console.info(formattedMessage, ...args);
          break;
        case LogLevel.WARN:
          window.console.warn(formattedMessage, ...args);
          break;
        case LogLevel.ERROR:
          window.console.error(formattedMessage, ...args);
          break;
      }
    }
  }

  private writeToLogFile<T extends unknown[]>(_message: string, ..._args: T): void {
    // Implement file logging logic if needed
    // This could write to a log file in the application's data directory
    // Placeholder for future implementation
  }

  public debug(message: string, ...args: unknown[]): void {
    this.log(LogLevel.DEBUG, message, ...args);
  }

  public info(message: string, ...args: unknown[]): void {
    this.log(LogLevel.INFO, message, ...args);
  }

  public warn(message: string, ...args: unknown[]): void {
    this.log(LogLevel.WARN, message, ...args);
  }

  public error(message: string, ...args: unknown[]): void {
    this.log(LogLevel.ERROR, message, ...args);
  }
}

function createLogger(config?: Partial<LoggerConfig>): Logger {
  return new Logger(config);
}

export { Logger, LogLevel, createLogger };

import { allChords } from "../allChords";
import { type Chord } from "../types/Types";

import { createHTMLElementFromHTML } from "./dom";

export class Phrase {
    private internalValue: string[];
    private internalChords: Chord[] = [];
    private internalChordsHTML: HTMLElement[] = [];

    constructor(value: string[]) {
        if(!value || !Array.isArray(value) || value.length == 0){
            throw new Error('Phrase value must be an array with at least one element');
        }
        this.internalValue = value;
        if(!value[0]) return;
        if (Array.isArray(value)) {
            this.setChords(value);
        }
    }

    get value(): string[] {
        return this.internalValue;
    }

    get chordsHTML(): HTMLElement[] {
        return this.internalChordsHTML;
    }

    get chords(): Chord[] {
        return this.internalChords;
    }

    private setChords(keys: string[]): void {
        keys.forEach((key) => {
            const foundChordHTML = Phrase.findChordHTML(key);
            if (foundChordHTML) {
                this.internalChordsHTML.push(foundChordHTML);
            }
            const chord = allChords.find(x => x.key == key);
            if (chord) {
                this.internalChords.push(chord);
            }
        })
    }

    public static createChordHTML(foundChord: Chord | undefined): HTMLElement | null {
        if (!foundChord) return null;
        return createHTMLElementFromHTML(
            `<div class="col-sm-2 row generated" id="chord2">
                <span id="char${foundChord.index}">${foundChord.key}</span>
                <img loading="lazy" alt="2" src="/images/svgs/${foundChord.chordCode}.svg" width="100" class="hand">
            </div>`
        )
    }

    public static findChordHTML(chordChar: string): HTMLElement | null {
        let inChord: HTMLElement | null = null;
        const foundChords
            = Array.from(allChords)
                .filter(x => {
                    return x.key
                        .replace('&#x2581;', ' ')
                        .replace('(underscore)', '_')
                        // .replace('Return (ENTER)', '\r')
                        == chordChar;
                });
        // Load the clone in Chord order into the wholePhraseChords div.
        if (foundChords.length > 0) {
            const foundChord = foundChords[0];
            const chordElement = this.createChordHTML(foundChord);
            if (chordElement && foundChord) {
                chordElement.setAttribute("name", foundChord.key.replace('(', '').replace(')', '').replace(' ', '-'));
                inChord = chordElement;
            }
        }
        // Removed console.error statement
        return inChord;
    }
}

import type React from 'react';
import { useRef, useEffect } from 'react';

export interface WebCamProps {
    setOn: boolean;
}

const WebCam: React.FC<WebCamProps> = ({ setOn }): JSX.Element => {
    const videoRef = useRef<HTMLVideoElement | null>(null);

    useEffect(() => {
        const videoElement = videoRef.current;
        if (!videoElement) return;

        videoElement.autoplay = true;
        videoElement.muted = true;
        videoElement.id = 'terminal-video';
        videoElement.setAttribute('playsinline', '');
        videoElement.setAttribute('webkit-playsinline', '');
        videoElement.setAttribute('x5-playsinline', '');

        if (setOn) {
            navigator.mediaDevices
                .getUserMedia({
                    video: {
                        facingMode: 'environment'
                    }
                })
                .then((stream: MediaStream) => {
                    if (videoElement) {
                        videoElement.srcObject = stream;
                    }
                })
                .catch((error: Error) => {
                    console.error('Error accessing media devices:', error);
                });
        } else {
            if (videoElement.srcObject) {
                const tracks = (videoElement.srcObject as MediaStream).getTracks();
                tracks.forEach(track => track.stop());
                videoElement.srcObject = null;
            }
        }

        videoElement.hidden = !setOn;

        return () => {
            if (videoElement && videoElement.srcObject) {
                const tracks = (videoElement.srcObject as MediaStream).getTracks();
                tracks.forEach(track => {
                    track.stop();
                    track.enabled = false;
                });
                videoElement.srcObject = null;
            }
        };
    }, [setOn]);

    return (
      <video ref={videoRef} style={{ width: '100%', height: 'auto' }}>
        <track kind="captions" src="" label="No captions available" />
      </video>
    );
};

export default WebCam;

