import { vi, beforeAll } from 'vitest';
import { test } from '@playwright/test';
import type { GamePhrase, ActivityType } from './src/types/Types';

declare global {
  interface Window {
    setNextTutorial: (tutorial: GamePhrase | null) => void;
    setActivity: (activity: ActivityType) => void;
    ActivityType: typeof ActivityType;
  }
}

// Initialize localStorage before any tests run
beforeAll(() => {
  if (typeof window.localStorage === 'undefined') {
    const store: Record<string, string | undefined> = {};
    window.localStorage = {
      getItem: (key: string) => store[key] ?? null,
      setItem: (key: string, value: string) => { store[key] = value; },
      removeItem: (key: string) => { store[key] = undefined; },
      clear: () => { Object.keys(store).forEach(key => { store[key] = undefined; }); },
      length: Object.keys(store).length,
      key: (index: number) => Object.keys(store)[index] ?? null
    };
  }
});

// Initialize localStorage and window methods for all tests
test.beforeEach(async ({ context }) => {
  await context.addInitScript(() => {
    // Initialize localStorage
    if (typeof window.localStorage === 'undefined') {
      const store: Record<string, string | undefined> = {};
      window.localStorage = {
        getItem: (key: string) => store[key] || null,
        setItem: (key: string, value: string) => { store[key] = value; },
        removeItem: (key: string) => { store[key] = undefined; },
        clear: () => { Object.keys(store).forEach(key => { store[key] = undefined; }); },
        length: Object.keys(store).length,
        key: (index: number) => Object.keys(store)[index] || null
      };
    }

    // Initialize tutorial state
    window.localStorage.setItem('tutorial-state', JSON.stringify({ currentStep: 0 }));

    // Initialize window methods
    if (typeof window.setActivity === 'undefined') {
      window.setActivity = vi.fn();
    }
    if (typeof window.setNextTutorial === 'undefined') {
      window.setNextTutorial = vi.fn();
    }
  });
});

// Suppress console warnings by mocking console methods
vi.spyOn(console, 'warn').mockImplementation(() => {});
vi.spyOn(console, 'error').mockImplementation(() => {});

import { useCallback, useEffect, useRef, useState } from 'react';

import { useComputed } from '@preact/signals-react';

import { HandTermWrapper, type IHandTermWrapperMethods } from './components/HandTermWrapper';
import { Output } from './components/Output';
import { ActivityMediatorProvider } from './contexts/ActivityMediatorContext';
import { AppProvider } from './contexts/AppContext';
import { CommandProvider } from './contexts/CommandProvider';
import { useAuth } from './hooks/useAuth';
import { bypassTutorialSignal } from './signals/appSignals';
import { TerminalCssClasses } from './types/TerminalTypes';
import { ActivityType } from './types/Types';
import { parseLocation } from './utils/navigationUtils';

export function App(): JSX.Element {
  const containerRef = useRef<HTMLDivElement>(null);
  const [containerWidth, setContainerWidth] = useState<number>(0);

  const auth = useAuth();
  const handexTermWrapperRef = useRef<IHandTermWrapperMethods>(null);
  const isBypassActive = useComputed(() => bypassTutorialSignal.value);

  const getContainerWidth = useCallback(() => {
    return containerRef.current?.clientWidth ?? 0
  }, [containerRef]);

  useEffect(() => {
    const handleResize = () => {
      const w = getContainerWidth();
      setContainerWidth(w);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [getContainerWidth]);

  useEffect(() => {
    const w = getContainerWidth();
    setContainerWidth(w);

    const handleOutsideTerminalClick = (event: MouseEvent | TouchEvent) => {
      const currentRef = handexTermWrapperRef.current;
      if (currentRef === null) return;

      // Check if the click is outside of the terminal area
      if ((event.target as HTMLElement).id !== TerminalCssClasses.Terminal) {
        event.stopPropagation();
        currentRef.focusTerminal();

        if (
          event instanceof MouseEvent ||
          (event instanceof TouchEvent && event.touches.length === 1)
        ) {
          setTimeout(() => {
            if (handexTermWrapperRef.current !== null) {
              handexTermWrapperRef.current.focusTerminal();
            }
          }, 1000);
        }
      }
    };

    // Attach the event listener to the document body
    document.body.addEventListener('click', handleOutsideTerminalClick);
    document.body.addEventListener('touchstart', handleOutsideTerminalClick);

    // Clean up the event listener
    return () => {
      document.body.removeEventListener('click', handleOutsideTerminalClick);
      document.body.removeEventListener('touchstart', handleOutsideTerminalClick);
    };
  }, [getContainerWidth]);

  return (
    <ActivityMediatorProvider>
      <div className='app' ref={containerRef}>
        <AppProvider>
          <CommandProvider
            auth={auth}
            handTermRef={handexTermWrapperRef}
          >
            {parseLocation().activityKey !== ActivityType.EDIT
              && <Output />
            }
            {isBypassActive.value && (
              <div style={{ position: 'fixed', top: 0, right: 0, color: 'black', background: '#222', padding: '5px' }}>
                Bypass Mode Active
              </div>
            )}
            <HandTermWrapper
              ref={handexTermWrapperRef}
              auth={auth}
              terminalWidth={containerWidth}
              onOutputUpdate={() => {}}
            />
          </CommandProvider>
        </AppProvider>
      </div>
    </ActivityMediatorProvider>
  );
}

import ReactDOMServer from 'react-dom/server';

import { Chord } from '../components/Chord';
import { commandRegistry } from './commandRegistry';
import { type ICommand, type ICommandResponse, type ICommandContext } from '../contexts/CommandContext';
import { type ParsedCommand } from '../types/Types';

export const HelpCommand: ICommand = {
  name: 'help',
  description: 'Display help information',
  execute: (
    context: ICommandContext,
    parsedCommand: ParsedCommand,
  ): Promise<ICommandResponse> => {
    if (
      parsedCommand.command === 'help'
      || parsedCommand.command === '411'
    ) {
      const commandChords = [
        'DELETE (Backspace)',
        'Return (ENTER)',
        'UpArrow',
        'LeftArrow',
        'DownArrow',
        'RightArrow',
        'ESCAPE',
      ].map(c => (
        <Chord key={c} displayChar={c} />
      ));
      const commandChordsHtml = commandChords.map(element => (
        ReactDOMServer.renderToStaticMarkup(element)
      )).join('');
      const commandList = commandRegistry.getHelp();
      const response = `
        <div class='chord-display-container'>${commandChordsHtml}</div>
        <div class='command-list-container'>
          <h3>Available Commands:</h3>
          <pre>${commandList}</pre>
        </div>
      `;
      return Promise.resolve<ICommandResponse>({ status: 200, message: response });
    }
    return Promise.resolve<ICommandResponse>({ status: 404, message: "Help command not recognized" });
  }
};

// src/commands/LoginCommand.ts

import { isInLoginProcessSignal, tempUserNameSignal, setTempUserName, setIsInLoginProcess } from 'src/signals/appSignals';

import { type ICommand, type ICommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { type ParsedCommand } from '../types/Types';
import { createLogger } from '../utils/Logger';

const logger = createLogger();

interface LoginErrorResponse {
    status: number;
    message: string;
}

export const LoginCommand: ICommand = {
    name: 'login',
    description: 'Log in to the system',
    execute: async (context: ICommandContext, parsedCommand: ParsedCommand): Promise<ICommandResponse> => {
        const { auth } = context;

        if (parsedCommand.args.length === 1) {
            // Start login process
            setIsInLoginProcess(true);
            const username = parsedCommand.args[0];
            setTempUserName(username);

            // Demonstrate usage of tempUserNameSignal
            if (tempUserNameSignal.value !== username) {
                logger.error('Username mismatch');
            }

            return { status: 200, message: 'Enter password:' };
        } else if (parsedCommand.args.length === 2 && isInLoginProcessSignal.value) {
            // Complete login process
            const tempUsername = parsedCommand.args[0];
            const password = parsedCommand.args[1];

            try {
                const result = await auth.login(tempUsername, password);
                setIsInLoginProcess(false);

                return {
                    status: result.status,
                    message: result.status === 200 ? 'Login successful!' : `Login failed: ${result.message}`,
                    sensitive: true // Mark as sensitive to mask password
                };
            } catch (error) {
                setIsInLoginProcess(false);

                // Type narrowing for error
                const errorResponse: LoginErrorResponse =
                    error instanceof Error
                    ? { status: 500, message: error.message }
                    : { status: 500, message: 'An unknown error occurred' };

                return {
                    status: errorResponse.status,
                    message: `Login error: ${errorResponse.message}`,
                    sensitive: true // Mark as sensitive to mask password
                };
            }
        } else {
            return { status: 400, message: 'Usage: login <username>' };
        }
    }
};

import ReactDOMServer from 'react-dom/server';

import { Chord } from '../components/Chord';
import { type ICommand, type ICommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { type ParsedCommand } from '../types/Types';

export const SpecialCommand: ICommand = {
  name: 'special',
  description: 'Display special characters',
  execute: async (
    _context: ICommandContext,
    _parsedCommand: ParsedCommand,
  ): Promise<ICommandResponse> => {
    await Promise.resolve();
    const specialChars = ['~', '`', '!', '@', '#', '$', '%', '^', '&', '*', '(', ')', '-', '_', '+', '=', '{', '[', '}', ']', '|', '\\', ':', ';', '"', "'", '<', '>', ',', '.', '?', '/'];
    const chordElements = specialChars.map(char => (
      <Chord key={char} displayChar={char} />
    ));
    const chordsHtml = chordElements.map(element =>
      ReactDOMServer.renderToStaticMarkup(element)
    ).join('');

    return {
      status: 200,
      message: `<div class='chord-display-container'>${chordsHtml}</div>`
    };
  }
};

// src/commands/clearCommand.tsx
import { type IHandTermWrapperMethods } from "../components/HandTermWrapper";
import { type ICommand, type ICommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { LogKeys } from '../types/TerminalTypes';
import { type ParsedCommand, type OutputElement } from '../types/Types';

interface HandTermState {
  commandHistory?: unknown[];
  outputElements?: unknown[];
}

interface HandTermInstance extends IHandTermWrapperMethods {
  setState: (state: Partial<HandTermState>) => void;
  terminalReset: () => void;
  prompt: () => void;
}

// Extend ICommandContext to include handTerm
interface ExtendedCommandContext extends ICommandContext {
  handTerm?: React.RefObject<HandTermInstance>;
  appendToOutput: (output: OutputElement) => void;
}

export const clearCommand: ICommand = {
  name: 'clear',
  description: 'Clear the command history',
  execute: async (
    context: ExtendedCommandContext,
    parsedCommand: ParsedCommand
  ): Promise<ICommandResponse> => {
    const handTerm = context.handTerm;

    if (!handTerm?.current) {
      const outputElement: OutputElement = {
        command: parsedCommand,
        response: 'No command context available.',
        status: 404,
        commandTime: new Date()
      };
      context.appendToOutput(outputElement);
      return { status: 404, message: 'No command context available.' };
    }

    // Logic to clear the command history from localStorage
    const removeKeys = Object.keys(localStorage).filter(key => {
      const matchesDefaultKeys = key.includes(LogKeys.Command ?? '') ||
        key.includes('terminalCommandHistory') ||
        key.includes(LogKeys.CharTime ?? '');

      const matchesArg = parsedCommand.args.length > 0 &&
        key.includes(parsedCommand.args[0] ?? '');

      return matchesDefaultKeys ?? matchesArg;
    });

    await Promise.all(removeKeys.map(async (removeKey) => {
      await new Promise<void>((resolve) => {
        localStorage.removeItem(removeKey); // Clear localStorage.length
        resolve();
      });
    }));

    const handTermInstance = handTerm.current;
    if (handTermInstance) {
      handTermInstance.setState({ commandHistory: [] });
      handTermInstance.setState({ outputElements: [] });
      handTermInstance.terminalReset();
      handTermInstance.prompt();
    }

    const outputElement: OutputElement = {
      command: parsedCommand,
      response: 'Command history cleared.',
      status: 200,
      commandTime: new Date()
    };
    context.appendToOutput(outputElement);

    return { status: 200, message: 'Command history cleared.' };
  }
};

// src/commands/commandRegistry.ts

import { type ICommand } from '../contexts/CommandContext';

export type ICommandRegistryItems = Record<string, ICommand>;

class CommandRegistry {
    private commands: ICommandRegistryItems = {};

    register(command: ICommand) {
        this.commands[command.name] = command;
    }

    getCommand(name: string): ICommand | undefined {
        return this.commands[name];
    }

    getHelp(filter?: string): string {
        return Object.values(this.commands)
            .filter(cmd => !filter || cmd.name.includes(filter))
            .map(cmd => `${cmd.name}: ${cmd.description}`)
            .join('\n\n');
    }

}

export const commandRegistry = new CommandRegistry();

// src/commands/index.ts
import { type ICommand } from '../contexts/CommandContext';

import { commandRegistry } from './commandRegistry';

// Interface for command modules
interface CommandModule {
  default?: ICommand;
  [key: string]: ICommand | undefined;
}

// Type guard to check if a value is a valid command
function isValidCommand(command: unknown): command is ICommand {
  return (
    typeof command === 'object' &&
    command !== null &&
    'name' in command &&
    'execute' in command &&
    typeof (command as ICommand).name === 'string' &&
    typeof (command as ICommand).execute === 'function'
  );
}

// Dynamically import and register all command files
const commandModules = import.meta.glob('./*Command.ts*', { eager: true });

Object.entries(commandModules).forEach(([_path, module]) => {
  const commandModule = module as CommandModule;

  // Check for default export first
  if (commandModule.default && isValidCommand(commandModule.default)) {
    commandRegistry.register(commandModule.default);
    return;
  }

  // If no default, check other exports
  const commands = Object.values(commandModule).filter(isValidCommand);
  commands.forEach(command => {
    commandRegistry.register(command);
  });
});

import type React from 'react';

import { type ICommand, type ICommandResponse, type ICommandContext } from '../contexts/CommandContext';
import { type IAuthProps } from '../hooks/useAuth';
import { ActivityType, type OutputElement, type ParsedCommand } from '../types/Types';
import { isNotNullOrUndefined } from '../utils/typeSafetyUtils';

// Extend the command context with optional methods
interface ExtendedCommandContext extends ICommandContext {
  auth: IAuthProps & {
    getFile?: (fileName: string, format: string) => Promise<{
      status: number;
      data?: string;
      message?: string;
    }>;
  };
  setEditMode?: (mode: boolean) => void;
  activityMediator?: {
    determineActivityState?: (activity: ActivityType) => void;
  };
  handleEditSave?: (content: string) => void;
}

export const wrtCommand: ICommand = {
  name: 'wrt',
  description: 'Write to the terminal',
  switches: {
    'file': 'Write the contents of a file to the terminal',
    'edit': 'Edit the contents of a file',
    'save': 'Save the contents of a file',
  },
  execute: async (
    context: ExtendedCommandContext,
    parsedCommand: ParsedCommand
  ): Promise<ICommandResponse> => {
    const switches = parsedCommand.switches;
    const args = parsedCommand.args;

    const getFileName = (switchKey: string): string | null => {
      const fileName = switches[switchKey];
      return typeof fileName === 'string' ? fileName : null;
    };

    const createOutputElement = (message: string | React.ReactNode): OutputElement => ({
      command: parsedCommand,
      response: message,
      status: 200,
      commandTime: new Date()
    });

    try {
      if (isNotNullOrUndefined(switches['file'])) {
        const fileName = getFileName('file');
        if (fileName && context.auth.getFile) {
          try {
            const response = await context.auth.getFile(fileName, 'txt');
            if (response.status === 200 && response.data) {
              context.appendToOutput(createOutputElement(response.data));
            } else {
              context.appendToOutput(createOutputElement(`Error reading file: ${fileName}`));
            }
          } catch (error) {
            const errorMessage = error instanceof Error ? error.message : 'Unknown error';
            context.appendToOutput(createOutputElement(`Error reading file: ${errorMessage}`));
          }
        } else {
          context.appendToOutput(createOutputElement('Please provide a file name or file reading is not supported.'));
        }
      } else if (switches['edit']) {
        const fileName = getFileName('edit');
        if (fileName && context.setEditMode && context.activityMediator?.determineActivityState) {
          context.setEditMode(true);
          context.activityMediator.determineActivityState(ActivityType.EDIT);
        } else {
          context.appendToOutput(createOutputElement('Please provide a file name to edit or edit mode is not supported.'));
        }
      } else if (switches['save']) {
        const fileName = getFileName('save');
        if (fileName && context.auth.getFile && context.handleEditSave) {
          try {
            const response = await context.auth.getFile(fileName, 'txt');
            if (response.status === 200 && response.data) {
              context.handleEditSave(response.data);
            } else {
              context.appendToOutput(createOutputElement(`Error reading file: ${fileName}`));
            }
          } catch (error) {
            const errorMessage = error instanceof Error ? error.message : 'Unknown error';
            context.appendToOutput(createOutputElement(`Error reading file: ${errorMessage}`));
          }
        } else {
          context.appendToOutput(createOutputElement('Please provide a file name to save or save functionality is not supported.'));
        }
      } else if (args.length > 0) {
        // Write the provided arguments to the terminal
        context.appendToOutput(createOutputElement(args.join(' ')));
      } else {
        return { status: 400, message: 'Please provide text to write or use a switch.' };
      }

      return { status: 200, message: 'Command executed successfully.' };
    } catch (error) {
      return {
        status: 500,
        message: error instanceof Error ? error.message : 'An unexpected error occurred'
      };
    }
  }
};

// src/components/CommandOutput.tsx

import {type ReactNode} from 'react';
import type React from 'react';

import { parsedCommandToString } from 'src/utils/commandUtils';

import { type ParsedCommand, type WPMs } from '../types/Types';

import { TimeDisplay } from './TimeDisplay';
import WpmTable from './WpmTable';

interface CommandOutputProps {
    command: ParsedCommand;
    response: ReactNode;
    status: number;
    wpms: WPMs;
    commandTime: Date;
}

export const CommandOutput: React.FC<CommandOutputProps> = ({
    command,
    response,
    status,
    wpms,
    commandTime
}) => {
    // Function to check if a string contains HTML tags
    const containsHTML = (str: string) => {
        return typeof str === 'string' && /<[a-z][\s\S]*>/i.test(str);
    };

    // Render response based on its type and content
    const renderResponse = () => {
        if (typeof response === 'string' && containsHTML(response)) {
            return <div className="response" dangerouslySetInnerHTML={{ __html: response }} />;
        }
        return <div className="response">{response}</div>;
    };

    return (
        <div data-status={status}>
            <div className="log-line">
                <span className="log-time">
                    <TimeDisplay time={commandTime} />
                </span>
                <span className="wpm-label">WPM:</span>
                <span className="wpm">{(wpms.wpmAverage ?? 0).toFixed(0)}</span>
                <span className="content">{parsedCommandToString(command)}</span>
            </div>
            {renderResponse()}
            {wpms && wpms.charWpms && wpms.charWpms.length > 0 && (
                <WpmTable wpms={wpms.charWpms} name="slow-char" />
            )}
        </div>
    );
}

import { useState, useImperativeHandle, forwardRef } from 'react';

import { Chord } from './Chord';

interface ErrorDisplayProps {
  mismatchedChar: string;
  isVisible: boolean;
}

const ErrorDisplay = forwardRef((props: ErrorDisplayProps, ref) => {
  const [errorCount, setErrorCount] = useState(0);
  const { mismatchedChar } = props;


  const showError = () => {
    setErrorCount(prevCount => prevCount + 1);
  };


  // Use useImperativeHandle to expose functions to the parent component
  useImperativeHandle(ref, () => ({
    showError,
  }));

  return (
    <div style={{ display: props.isVisible ? 'block' : 'none' }} >
      <div>Error Count: {errorCount}</div>
      {mismatchedChar &&
        <Chord displayChar={mismatchedChar} />
      }
    </div>
  );
});

ErrorDisplay.displayName = 'ErrorDisplay';

export default ErrorDisplay;

