import React, { useState, useEffect, useImperativeHandle, forwardRef, type ForwardRefRenderFunction } from 'react';

export interface TimerProps {}

export interface TimerHandle {
  start: () => void;
  stop: () => void;
  reset: () => number;
  success: () => void;
}

const Timer: ForwardRefRenderFunction<TimerHandle, TimerProps> = (_props, ref) => {
  const [centiSecond, setCentiSecond] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const [svgStatus, setSvgStatus] = useState<'start' | 'stop' | 'pause'>('start');

  useEffect(() => {
    let intervalId: number | null = null;

    if (isActive) {
      setSvgStatus('pause');
      intervalId = window.setInterval(() => {
        setCentiSecond((prevCentiSecond: number) => prevCentiSecond + 1);
      }, 10);
    } else if (!isActive && centiSecond !== 0) {
      setSvgStatus('start');
      if (intervalId) clearInterval(intervalId);
    }

    return () => {
      if (intervalId) clearInterval(intervalId);
    };
  }, [isActive, centiSecond]);

  const start = () => {
    setIsActive(true);
  };

  const stop = () => {
    setIsActive(false);
  };

  const reset = (): number => {
    setIsActive(false);
    const finalCentiSecond = centiSecond;
    setCentiSecond(0);
    return finalCentiSecond;
  };

  const success = () => {
    setCentiSecond(0);
    setIsActive(false);
  };

  useImperativeHandle(ref, () => ({
    start,
    stop,
    reset,
    success
  }));

  const renderSvg = () => {
    switch (svgStatus) {
      case 'start':
        return <use href="#start" transform="scale(2,2)" />;
      case 'stop':
        return <use href="#stop" transform="scale(2,2)" />;
      case 'pause':
        return <use href="#pause" transform="scale(2,2)" />;
      default:
        return <use href="#stop" transform="scale(2,2)" />;
    }
  };

  return (
    <React.Fragment>
      <span id="timer">{(centiSecond / 100).toFixed(1)}</span>
      <svg width="15" height="20" style={{ float: 'left' }}>
        {renderSvg()}
      </svg>
    </React.Fragment>
  );
};

export default forwardRef(Timer);

// src/components/WpmTable.tsx
import type React from 'react';
import { useMemo } from 'react';

import { type CharWPM } from '../types/TerminalTypes';

interface WpmTableProps {
  wpms: ReadonlyArray<CharWPM>;
  name?: string;
}

export const WpmTable: React.FC<WpmTableProps> = ({ wpms, name = "slowest-characters" }) => {
  const groupedAndSortedWpms = useMemo(() => {
    // Group by character
    const grouped = wpms.reduce((acc, curr) => {
      const char = curr.character.replace("\r", "\\r").replace(" ", "\\s");
      if (!acc[char]) {
        acc[char] = { character: char, wpm: curr.wpm, count: 1 };
      } else {
        acc[char].wpm += curr.wpm;
        acc[char].count += 1;
      }
      return acc;
    }, {} as Record<string, { character: string, wpm: number, count: number }>);

    // Calculate average WPM for each character
    const averages = Object.values(grouped).map(item => ({
      character: item.character,
      wpm: item.wpm / item.count
    }));

    // Sort by slowest (lowest WPM) first
    averages.sort((a, b) => a.wpm - b.wpm);

    // Take top 5
    return averages.slice(0, 5);
  }, [wpms]);

  return (
    <table className="wpm-table">
      <thead>
        <tr><th colSpan={2}>{name}</th></tr>
        <tr>
          <th>Character</th>
          <th>Avg WPM</th>
        </tr>
      </thead>
      <tbody>
        {groupedAndSortedWpms.map((wpm, index) => (
          <tr key={index} className="wpm-table-row">
            <td>{wpm.character}</td>
            <td className="number">{wpm.wpm.toFixed(0)}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default WpmTable;

