import { type Page, type Locator, expect } from '@playwright/test';

import { TERMINAL_CONSTANTS } from 'src/constants/terminal';
import type { Signal } from '@preact/signals-react';
import type { ActivityType, GamePhrase } from 'src/types/Types';
import { TEST_CONFIG } from '../config';

// Extend Window interface for our signals
declare global {
  interface Window {
    tutorialSignal: Signal<GamePhrase | null>;
    activitySignal: Signal<ActivityType>;
  }
}

export class TerminalPage {
  readonly page: Page;
  readonly terminal: Locator;
  readonly output: Locator;
  readonly tutorialMode: Locator;
  readonly gameMode: Locator;
  readonly nextChars: Locator;
  private readonly prompt = TERMINAL_CONSTANTS.PROMPT;

  async waitForActivityTransition(timeout = 10000): Promise<void> {
    try {
      await this.page.waitForSelector('#terminal-game', { state: 'visible', timeout });
      await this.page.waitForSelector('.tutorial-component', { state: 'hidden', timeout });
    } catch (error) {
      const gameVisible = await this.gameMode.isVisible();
      const tutorialVisible = await this.tutorialMode.isVisible();
      console.log('Activity transition failed. Current state:', { gameVisible, tutorialVisible });
      throw error;
    }
  }

  async waitForTutorialMode(timeout = 10000): Promise<void> {
    try {
      // Wait for tutorial component to be attached
      await this.page.waitForSelector('.tutorial-component', { state: 'attached', timeout });

      // Wait for tutorial content to be loaded (tutorial-prompt only appears when content is set)
      await this.page.waitForSelector('.tutorial-prompt', { state: 'visible', timeout });

      // Get current tutorial state for debugging
      const tutorialState = await this.page.evaluate(() => ({
        tutorialSignal: window.tutorialSignal?.value,
        activitySignal: window.activitySignal?.value,
        completedTutorials: localStorage.getItem('completed-tutorials'),
        tutorialState: localStorage.getItem('tutorial-state')
      }));

      console.log('[Tutorial State]', tutorialState);

      // Verify tutorial content is actually set
      const tutorialContent = await this.page.locator('.tutorial-prompt').textContent();
      if (!tutorialContent) {
        throw new Error('Tutorial content is empty');
      }
    } catch (error) {
      // Log the current page state for debugging
      const html = await this.page.content();
      console.log('[Page Content]', html);
      throw error;
    }
  }

  constructor(page: Page) {
    this.page = page;
    this.terminal = page.locator('#xtermRef');
    this.output = page.locator('#output-container');
    this.tutorialMode = page.locator('.tutorial-component');
    this.gameMode = page.locator('#terminal-game');
    this.nextChars = page.locator('pre#next-chars');
    return this;
  }

  public async goto(): Promise<void> {
    await this.page.goto(`${TEST_CONFIG.baseUrl}/`);
    // Wait for the signal to be exposed
    await this.page.waitForFunction(() => 'commandLineSignal' in window, { timeout: TEST_CONFIG.timeout.medium });
    await this.waitForTerminal();
    await this.waitForPrompt();
  }

  /**
   * Types a command into the terminal
   * @param command The command to type
   */
  public async typeCommand(command: string): Promise<void> {
    await this.waitForTerminal();
    await this.terminal.click();
    await this.page.keyboard.type(command);
  }

  /**
   * Types a sequence of keys without executing a command
   * @param keys The keys to type
   */
  public async typeKeys(keys: string): Promise<void> {
    await this.waitForTerminal();
    await this.terminal.click();
    await this.page.keyboard.type(keys);
  }

  /**
   * Presses the Enter key
   */
  public async pressEnter(): Promise<void> {
    await this.waitForTerminal();
    await this.page.keyboard.press('Enter');
  }

  /**
   * Executes a command by typing it and pressing Enter
   * @param command The command to execute
   */
  public async executeCommand(command: string): Promise<void> {
    await this.waitForTerminal();
    await this.typeCommand(command);
    await this.pressEnter();
  }

  /**
   * Gets the current terminal output
   * @returns The text content of the output container
   */
  public async getOutput(): Promise<string> {
    return await this.output.textContent() ?? '';
  }

  /**
   * Gets the current command line text (without the prompt)
   * @returns The current command line text
   */
  public async getCurrentCommand(): Promise<string> {
    // Wait for the signal to be available
    await this.page.waitForFunction(() => 'commandLineSignal' in window);

    // Get the value from commandLineSignal
    const commandLine = await this.page.evaluate(() => {
      return (window as unknown as { commandLineSignal: { value: string } }).commandLineSignal.value;
    });

    return commandLine ?? '';
  }

  /**
   * Waits for specific text to appear in the output
   * @param text The text to wait for
   */
  public async waitForOutput(text: string): Promise<void> {
    await this.output.getByText(text, { exact: false }).waitFor();
  }

  /**
   * Waits for specific text to appear in the next chars display
   * @param text The text to wait for
   */
  public async waitForNextChars(text: string): Promise<void> {
    // First wait for the element to exist
    await this.nextChars.waitFor({ state: 'attached' });

    // Then wait for the specific text
    await this.nextChars.waitFor({ state: 'visible' });
    await expect(this.nextChars).toHaveText(text, { timeout: 10000 });
  }

  /**
   * Waits for the prompt to appear, indicating the terminal is ready
   */
  public async waitForPrompt(): Promise<void> {
    await this.terminal.getByText(this.prompt).last().waitFor();
  }

  /**
   * Waits for the terminal to be ready
   */
  public async waitForTerminal(): Promise<void> {
    // Wait for application to load
    await this.page.waitForSelector('#handterm-wrapper', { state: 'attached', timeout: TEST_CONFIG.timeout.long });

    // Wait for terminal element
    await this.page.waitForSelector('#xtermRef', { state: 'attached', timeout: TEST_CONFIG.timeout.medium });

    // Wait for xterm.js to be loaded and initialized
    await this.page.waitForFunction(() => {
      const term = document.querySelector('#xtermRef');
      if (term == null) return false;
      const screen = term.querySelector('.xterm-screen');
      return term.querySelector('.xterm-viewport') !== null &&
        screen !== null &&
        screen.childElementCount > 0;
    }, { timeout: TEST_CONFIG.timeout.medium });

    // Wait for terminal to be visible and interactive
    await this.terminal.waitFor({ state: 'visible', timeout: TEST_CONFIG.timeout.medium });

    // Wait for terminal to be ready for interaction and focused
    await this.page.waitForFunction(() => {
      const term = document.querySelector('#xtermRef');
      if (term == null) return false;
      const screen = term.querySelector('.xterm-screen');
      if (screen == null) return false;
      const viewport = term.querySelector('.xterm-viewport');
      if (viewport == null) return false;
      return screen.childElementCount > 0;
    }, { timeout: TEST_CONFIG.timeout.medium });

    // Focus the terminal
    await this.terminal.click();
  }

  /**
   * Focuses the terminal
   */
  public async focus(): Promise<void> {
    await this.terminal.click();
  }

  /**
   * Clears the current command line using Ctrl+C
   */
  public async clearLine(): Promise<void> {
    await this.terminal.click();
    await this.page.keyboard.press('Control+C');
    await this.waitForPrompt();
  }
}

import { test as playwrightTest } from '@playwright/test';
import type { GamePhrase, ActivityType } from '../types/Types';

declare global {
  interface Window {
    testName: string;
  }
}

// Initialize localStorage and window methods for all e2e tests
export const setupPlaywrightTests = (): void => {
  playwrightTest.beforeEach(async ({ context }, _testInfo) => {
    await context.addInitScript((title: string) => {
      window.testName = title;
      // Initialize localStorage
      if (typeof window.localStorage === 'undefined') {
        const store: Record<string, string | undefined> = {};
        window.localStorage = {
          getItem: (key: string) => store[key] || null,
          setItem: (key: string, value: string) => { store[key] = value; },
          removeItem: (key: string) => { store[key] = undefined; },
          clear: () => { Object.keys(store).forEach(key => { store[key] = undefined; }); },
          length: Object.keys(store).length,
          key: (index: number) => Object.keys(store)[index] || null
        };
      }

      // Initialize tutorial state based on test name
      const testName = window.testName;
      if (testName.includes('should handle \\r')) {
        window.localStorage.setItem('completed-tutorials', JSON.stringify([]));
      } else if (testName.includes('should handle fdsa\\r')) {
        window.localStorage.setItem('completed-tutorials', JSON.stringify(['\\r']));
      } else if (testName.includes('should handle jkl;\\r')) {
        window.localStorage.setItem('completed-tutorials', JSON.stringify(['\\r', 'fdsa']));
      }
      window.localStorage.setItem('tutorial-state', JSON.stringify({ currentStep: 0 }));

      // Initialize window methods
      if (typeof window.setActivity === 'undefined') {
        window.setActivity = (_activity: ActivityType) => {};
      }
      if (typeof window.setNextTutorial === 'undefined') {
        window.setNextTutorial = (_tutorial: GamePhrase | null) => {};
      }
    });
  });
};

// TerminalGame.ts
import { useState, useEffect, useRef, useImperativeHandle, useCallback, useMemo, forwardRef } from 'react';

import confetti from 'canvas-confetti';

import { useComputed, useSignalEffect } from "@preact/signals-react";

import { commandLineSignal } from "../signals/commandLineSignals";
import { isInGameModeSignal } from '../signals/gameSignals';
import { createLogger, LogLevel } from '../utils/Logger';

import { Hero } from './Hero';
import { layers, getLevelCount } from './Level';
import { type IParallaxLayer, ParallaxLayer } from './ParallaxLayer';
import ScrollingTextLayer from './ScrollingTextLayer';
import { type Sprite } from './sprites/Sprite';
import { type Action, type ActionType } from './types/ActionTypes';
import { type SpritePosition } from './types/Position';
import { Zombie4 } from './Zombie4';

const logger = createLogger({
  prefix: 'Game',
  level: LogLevel.DEBUG
});

interface ICharacterRefMethods {
  getCurrentSprite: () => Sprite | null;
  getActions: () => Record<ActionType, Action>;
  positionRef: SpritePosition;
  draw: (context: CanvasRenderingContext2D, position: SpritePosition) => number;
}

interface IGameProps {
  canvasHeight: number;
  canvasWidth: number;
}

interface IGameHandle {
  startGame: (tutorialGroup?: string) => void;
  completeGame: () => void;
  resetGame: () => void;
  levelUp: (setLevelValue?: number | null) => void;
}

function GameFunction(props: IGameProps, ref: React.ForwardedRef<IGameHandle>): JSX.Element {
  const {
    canvasHeight,
    canvasWidth,
  } = props;

  // Use useMemo to memoize static objects
  const zombie4StartPosition = useMemo(() => ({ leftX: 0, topY: 0 }), []);
  const heroStartPosition = useMemo(() => ({ leftX: 165, topY: 29 }), []);
  const heroXPercent = 0.23;

  const zombie4PositionRef = useRef<SpritePosition>(zombie4StartPosition);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const heroRef = useRef<ICharacterRefMethods>(null);
  const zombie4Ref = useRef<ICharacterRefMethods>(null);
  const animationFrameIndex = useRef<number | undefined>(undefined);
  const zombie4DeathTimeout = useRef<NodeJS.Timeout | null>(null);
  const heroRunTimeoutRef = useRef<number | null>(null);

  const heroPositionRef = useRef<SpritePosition>(heroStartPosition);
  const [heroPosition, setHeroPosition] = useState<SpritePosition>({ leftX: canvasWidth * heroXPercent, topY: 30 });

  const [currentLevel, setCurrentLevel] = useState(1);
  const [context, setContext] = useState<CanvasRenderingContext2D | null>(null);
  const [backgroundOffsetX, setBackgroundOffsetX] = useState(0);
  const [isPhraseComplete, setIsPhraseComplete] = useState(false);
  const [isTextScrolling, setIsTextScrolling] = useState(false);
  const [heroAction, setHeroAction] = useState<ActionType>('Idle');
  const [zombie4Action, setZombie4Action] = useState<ActionType>('Walk');
  const textToScroll = "TERMINAL VELOCITY!";
  const [layersState, setLayersState] = useState<IParallaxLayer[]>(layers[0] ?? []);

  const commandLine = useComputed(() => commandLineSignal.value);
  const isInGameMode = useComputed(() => isInGameModeSignal.value).value;

  // Memoize getLevel to prevent unnecessary re-renders
  const getLevel = useCallback(() => currentLevel, [currentLevel]);

  const stopAnimationLoop = useCallback(() => {
    const frameId = animationFrameIndex.current;
    if (typeof frameId === 'number' && !Number.isNaN(frameId)) {
      cancelAnimationFrame(frameId);
      animationFrameIndex.current = undefined;
    }
  }, []);

  const triggerConfettiCannon = useCallback(() => {
    void confetti({
      zIndex: 3,
      angle: 160,
      spread: 45,
      startVelocity: 45,
      particleCount: 150,
      origin: { x: 0.99, y: 0.8 }
    });
  }, []);

  const setZombie4ToDeathThenResetPosition = useCallback(() => {
    const timeout = zombie4DeathTimeout.current;
    if (timeout !== null) {
      clearTimeout(timeout);
      zombie4DeathTimeout.current = null;
    }

    setZombie4Action('Death');
    zombie4DeathTimeout.current = setTimeout(() => {
      setZombie4Action('Walk');
      zombie4PositionRef.current = zombie4StartPosition;
      setIsPhraseComplete(false);
      zombie4DeathTimeout.current = null;
    }, 3000);
  }, [zombie4StartPosition]);

  const updateCharacterAndBackgroundPostion = useCallback((context: CanvasRenderingContext2D): number => {
    const canvasCenterX = canvasWidth * heroXPercent;
    const characterReachThreshold = canvasCenterX;

    context.clearRect(0, 0, canvasWidth, canvasHeight);

    let heroDx = 0;
    const hero = heroRef.current;
    if (hero !== null) {
      heroDx = hero.draw(context, heroPosition);
    }

    const zombie = zombie4Ref.current;
    if (zombie !== null) {
      const zombie4Dx = zombie.draw(context, zombie4PositionRef.current);
      zombie4PositionRef.current = {
        ...zombie4PositionRef.current,
        leftX: zombie4PositionRef.current.leftX + zombie4Dx - heroDx
      };
    }

    if (heroDx !== 0) {
      setBackgroundOffsetX(prev => prev + heroDx);

      if (heroPosition.leftX >= characterReachThreshold) {
        setHeroPosition(prev => ({ ...prev, leftX: characterReachThreshold }));
      }

      const newZombie4PositionX = zombie4PositionRef.current.leftX - heroDx;
      zombie4PositionRef.current = {
        ...zombie4PositionRef.current,
        leftX: newZombie4PositionX
      };
    }
    return heroDx;
  }, [canvasWidth, canvasHeight, heroPosition]);

  const checkProximityAndSetAction = useCallback(() => {
    const ATTACK_THRESHOLD = 100;
    const distance = heroPosition.leftX - zombie4PositionRef.current.leftX;

    if (-20 < distance && distance < ATTACK_THRESHOLD) {
      setZombie4Action('Attack');
      if (distance < 50) {
        setHeroAction('Hurt');
      }
      if (distance < 30) {
        setHeroAction('Death');
      }
    } else {
      if (zombie4Action === 'Attack') {
        setZombie4Action('Walk');
      }
    }
  }, [heroPosition, zombie4Action, setHeroAction]);

  const toggleScrollingText = useCallback((show: boolean | null = null) => {
    if (show === null) show = !isTextScrolling;
    setIsTextScrolling(show);
  }, [isTextScrolling]);

  const drawScrollingText = useCallback(() => {
    toggleScrollingText(true);
    setTimeout(() => {
      toggleScrollingText(false);
    }, 3000);
  }, [toggleScrollingText]);

  const startAnimationLoop = useCallback((context: CanvasRenderingContext2D) => {
    const frameDelay = 150;
    let lastFrameTime = performance.now();

    const loop = () => {
      const now = performance.now();
      const deltaTime = now - lastFrameTime;

      if (typeof deltaTime === 'number' && !Number.isNaN(deltaTime) && deltaTime >= frameDelay) {
        lastFrameTime = now - (deltaTime % frameDelay);

        if (isPhraseComplete) {
          drawScrollingText();
        }

        updateCharacterAndBackgroundPostion(context);
        checkProximityAndSetAction();
      }
      animationFrameIndex.current = requestAnimationFrame(loop);
    };

    animationFrameIndex.current = requestAnimationFrame(loop);
  }, [isPhraseComplete, drawScrollingText, updateCharacterAndBackgroundPostion, checkProximityAndSetAction]);

  const setHeroRunAction = useCallback(() => {
    const timeout = heroRunTimeoutRef.current;
    if (timeout !== null) {
      clearTimeout(timeout);
      heroRunTimeoutRef.current = null;
    }

    setHeroAction('Run');
    heroRunTimeoutRef.current = window.setTimeout(() => {
      setHeroAction('Idle');
      heroRunTimeoutRef.current = null;
    }, 800);
  }, [setHeroAction]);

  const handleCommandLineChange = useCallback(() => {
    setHeroRunAction();
  }, [setHeroRunAction]);

  useSignalEffect(() => {
    if (commandLine.value?.trim() !== '') {
      handleCommandLineChange();
    }
  });

  const setupCanvas = useCallback((canvas: HTMLCanvasElement) => {
    const canvasContext = canvas.getContext('2d');
    if (canvasContext !== null) {
      setContext(canvasContext);
    } else {
      logger.error("Failed to get canvas context.");
    }
  }, []);

  const setLevel = useCallback((newLevel: number) => {
    const levelIndex = Math.max(0, Math.min(newLevel - 1, layers.length - 1));
    const newLayers = layers[levelIndex] ?? [];
    setCurrentLevel(newLevel);
    setLayersState(newLayers);
  }, []);

  const levelUp = useCallback((setLevelValue: number | null = null) => {
    const levelCount = getLevelCount();
    if (setLevelValue !== null && setLevelValue > levelCount) {
      setLevelValue = levelCount;
    }
    let nextLevel = setLevelValue !== null ? setLevelValue : getLevel() + 1;
    if (nextLevel > levelCount) nextLevel = 0;
    if (nextLevel < 1) nextLevel = 1;
    setLevel(nextLevel);
  }, [setLevel, getLevel]);

  const startGame = useCallback(() => {
    if (context !== null) {
      startAnimationLoop(context);
    }
    setIsPhraseComplete(false);
  }, [context, startAnimationLoop]);

  const completeGame = useCallback(() => {
    setZombie4ToDeathThenResetPosition();
    triggerConfettiCannon();
    setIsPhraseComplete(true);
  }, [setZombie4ToDeathThenResetPosition, triggerConfettiCannon]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas !== null) {
      setupCanvas(canvas);
    }

    return () => {
      stopAnimationLoop();
      const timeout = zombie4DeathTimeout.current;
      if (timeout !== null) {
        clearTimeout(timeout);
      }
    };
  }, [setupCanvas, stopAnimationLoop]);

  useEffect(() => {
    if (context !== null) {
      startAnimationLoop(context);
    }
  }, [context, startAnimationLoop]);

  useImperativeHandle(ref, () => ({
    startGame,
    completeGame,
    resetGame: () => {
      zombie4PositionRef.current = zombie4StartPosition;
      setIsPhraseComplete(false);
    },
    levelUp,
  }), [startGame, completeGame, levelUp, zombie4StartPosition]);

  if (!isInGameMode) {
    return <div />;
  }

  return (
    <div
      id="terminal-game"
      style={{ position: "relative", height: canvasHeight }}
    >
      <div className="parallax-background">
        {isTextScrolling && (
          <ScrollingTextLayer
            text={textToScroll}
            canvasHeight={canvasHeight}
          />
        )}
        {layersState.map((layer, index) => (
          <ParallaxLayer
            key={index}
            layer={layer}
            offset={backgroundOffsetX}
            canvasHeight={canvasHeight}
          />
        ))}
      </div>
      <canvas
        data-testid="game-canvas"
        style={{ position: "absolute", top: 0, left: 0, zIndex: 2 }}
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
      />
      <Hero
        ref={heroRef}
        positionRef={heroPositionRef}
        currentActionType={heroAction}
        scale={1.95}
      />
      <Zombie4
        ref={zombie4Ref}
        positionRef={zombie4PositionRef}
        currentActionType={zombie4Action}
        scale={1.90}
      />
    </div>
  );
}

export type { IGameHandle, IGameProps };
export const Game = forwardRef(GameFunction);

// SpriteManagerContext.tsx
import React from 'react';

import { type SpriteManager } from './sprites/SpriteManager';

// Create a context with a default undefined value
const SpriteManagerContext = React.createContext<SpriteManager | undefined>(undefined);

export default SpriteManagerContext;

import { createLogger, LogLevel } from 'src/utils/Logger';

import { type SpriteAnimation } from '../types/SpriteTypes';

import { Sprite } from './Sprite';

const logger = createLogger({
  prefix: 'SpriteManager',
  level: LogLevel.ERROR
});

export class SpriteManager {
    private spriteCache = new Map<string, Sprite>();

    public async loadSprite(animationData: SpriteAnimation): Promise<Sprite> {
        if (this.spriteCache.has(animationData.imagePath)) {
            const cachedSprite = this.spriteCache.get(animationData.imagePath);
            if (cachedSprite !== undefined) {
                return cachedSprite;
            }
        }

        const sprite = new Sprite(animationData.imagePath, animationData.frameCount, animationData.frameWidth, animationData.frameHeight, animationData.framePositions);
        await this.waitForManagerImageLoad(sprite.image);

        this.spriteCache.set(animationData.imagePath, sprite);
        return sprite;
    }

    private waitForManagerImageLoad(image: HTMLImageElement): Promise<void> {
        return new Promise((resolve, reject) => {
            const onLoad = () => {
                // Cleanup: Remove both event listeners
                image.removeEventListener('load', onLoad);
                image.removeEventListener('error', onError);
                resolve();
            };

            const onError = () => {
                // Cleanup: Remove both event listeners
                image.removeEventListener('load', onLoad);
                image.removeEventListener('error', onError);
                logger.error('Image failed to load: ' + image.src);
                reject(new Error(`Image failed to load: ${image.src}`)); // Reject the promise on error
            };

            // Attach the event listeners
            image.addEventListener('load', onLoad);
            image.addEventListener('error', onError);
        });
    }
}

import { useCallback } from 'react';
import axios, { type AxiosError } from 'axios';

import ENDPOINTS from '../shared/endpoints.json';
import { type IAuthProps } from './useAuth';

interface APIResponse<T> {
    status: number;
    data?: T;
    error?: string;
}

interface FileContentResponse {
    content: string;
    encoding?: string;
    sha?: string;
}

interface TreeItemResponse {
    path: string;
    type: string;
    sha?: string;
    size?: number;
}

interface RepoResponse {
    name: string;
    description?: string;
    private: boolean;
    default_branch: string;
}

interface APIError {
    status: number;
    message: string;
}

export function useAPI(auth: IAuthProps): {
    getRepoTree: (repo: string, path?: string, sha?: string) => Promise<APIResponse<TreeItemResponse[]>>,
    getFileContent: (repo: string, path: string) => Promise<APIResponse<FileContentResponse>>,
    listRecentRepos: () => Promise<APIResponse<RepoResponse[]>>
} {
    const makeAuthenticatedRequest = useCallback(async <T,>(
        endpoint: string,
        params?: Record<string, string>,
        method: 'GET' | 'POST' = 'GET'
    ): Promise<APIResponse<T>> => {
        try {
            const authResponse = await auth.validateAndRefreshToken();
            if (authResponse == null || authResponse.status !== 200 || authResponse.data == null) {
                return {
                    status: 401,
                    error: 'Authentication failed'
                };
            }

            const response = await axios({
                method,
                url: `${ENDPOINTS.api.BaseUrl}${endpoint}`,
                headers: {
                    'Authorization': `Bearer ${authResponse.data.accessToken}`,
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                params
            });

            return {
                status: response.status,
                data: response.data as T
            };
        } catch (error) {
            const apiError = error as AxiosError<APIError>;

            // Log error without using console.error
            const errorMessage = apiError.response?.data?.message ||
                                 apiError.message ||
                                 'Request failed';

            if (typeof window !== 'undefined' && window.console && window.console.error) {
                window.console.error('API request failed:', errorMessage);
            }

            return {
                status: apiError.response?.status || 500,
                error: errorMessage
            };
        }
    }, [auth.validateAndRefreshToken]);

    const getRepoTree = useCallback(async (repo: string, path?: string, sha?: string) => {
        return makeAuthenticatedRequest<TreeItemResponse[]>(ENDPOINTS.api.GetRepoTree, {
            repo,
            ...(path && { path }),
            ...(sha && { sha })
        });
    }, [makeAuthenticatedRequest]);

    const getFileContent = useCallback(async (repo: string, path: string) => {
        return makeAuthenticatedRequest<FileContentResponse>('/github/file', {
            repo,
            path
        });
    }, [makeAuthenticatedRequest]);

    const listRecentRepos = useCallback(async () => {
        return makeAuthenticatedRequest<RepoResponse[]>(ENDPOINTS.api.ListRecentRepos);
    }, [makeAuthenticatedRequest]);

    return {
        getRepoTree,
        getFileContent,
        listRecentRepos
    };
}

// src/hooks/useCommand.ts
import { useComputed } from '@preact/signals-react';
import type React from 'react';
import { useContext, useState, useCallback, useEffect } from 'react';

import { activitySignal, appendToOutput } from 'src/signals/appSignals';
import { setCommandTime } from 'src/signals/commandLineSignals';
import { createLogger } from 'src/utils/Logger';

import { commandRegistry } from '../commands/commandRegistry';
import { CommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { LogKeys } from '../types/TerminalTypes';
import { ActivityType, type OutputElement, type ParsedCommand } from '../types/Types';
import { parsedCommandToString, loadCommandHistory, saveCommandHistory } from '../utils/commandUtils';

import { useActivityMediator } from './useActivityMediator';
import { useWPMCalculator } from './useWPMCaculator';


const logger: ReturnType<typeof createLogger> = createLogger({ prefix: 'useCommand' });

export const useCommand = (): {
    output: OutputElement[];
    resetOutput: () => void;
    commandHistory: string[];
    addToCommandHistory: (command: ParsedCommand | string) => void;
    getCommandResponseHistory: () => string[];
    handleCommand: (parsedCommand: ParsedCommand) => Promise<void>;
    commandHistoryIndex: number;
    setCommandHistoryIndex: React.Dispatch<React.SetStateAction<number>>;
    commandHistoryFilter: string | null;
    setCommandHistoryFilter: React.Dispatch<React.SetStateAction<string | null>>;
    appendToOutput: (outputElement: OutputElement) => void;
    executeCommand: (parsedCommand: ParsedCommand) => Promise<void>;
} => {
    const [output, setOutput] = useState<OutputElement[]>([]);
    const [commandHistory, setCommandHistory] = useState<string[]>([]);
    const [commandHistoryIndex, setCommandHistoryIndex] = useState(-1);
    const [commandHistoryFilter, setCommandHistoryFilter] = useState<string | null>(null);
    const currentActivity = useComputed(() => activitySignal.value);
    const {
        handleCommandExecuted,
        checkTutorialProgress
    } = useActivityMediator();
    const wpmCalculator = useWPMCalculator();
    const context = useContext(CommandContext);

    if (context === undefined) {
        throw new Error('useCommand must be used within a CommandProvider');
    }

    // Load command history from localStorage on mount
    useEffect(() => {
        const savedHistory = loadCommandHistory();
        if (savedHistory && savedHistory.length > 0) {
            logger.debug('Loaded command history:', savedHistory);
            setCommandHistory(savedHistory);
        }
    }, []);

    const resetOutput = useCallback((): void => {
        logger.debug('Resetting output');
        setOutput([]);
    }, []);

    const addToCommandHistory = useCallback((command: ParsedCommand | string): void => {
        const commandString = typeof command === 'string'
            ? command
            : parsedCommandToString(command);

        logger.debug('Adding to command history:', commandString);
        setCommandHistory(prev => {
            // Don't add if it's identical to the previous command
            if (prev.length > 0 && prev[prev.length - 1] === commandString) {
                return prev;
            }
            const newHistory = [...prev, commandString].slice(-120); // Keep last 120 commands
            saveCommandHistory(newHistory); // Save to localStorage whenever history changes
            return newHistory;
        });
    }, []);

    const getCommandResponseHistory = useCallback((): string[] => {
        const keys: string[] = [];
        const history: string[] = [];
        for (let i = 0; i < localStorage.length; i++) {
            const key = localStorage.key(i);
            if (key?.startsWith(LogKeys.Command + '_')) {
                keys.push(key);
            }
        }
        keys.sort();
        for (const key of keys) {
            const historyJSON = localStorage.getItem(key);
            if (historyJSON) {
                history.push(historyJSON);
            }
        }
        logger.debug('Retrieved command response history:', history);
        return history;
    }, []);

    const createCommandRecord = useCallback((
        command: ParsedCommand,
        response: React.ReactNode,
        status: number,
        commandTime: Date,
        sensitive: boolean
    ): OutputElement => {
        const wpms = wpmCalculator.getWPMs();
        logger.debug('Creating command record:', { command, response, status });
        return {
            command,
            response,
            status,
            wpmAverage: wpms.wpmAverage,
            characterAverages: wpms.charWpms,
            commandTime,
            sensitive: sensitive ?? false
        };
    }, [wpmCalculator]);

    const processCommandOutput = useCallback((
        command: ParsedCommand,
        response: ICommandResponse,
    ): void => {
        logger.debug('Processing command output:', { command, response });
        const commandTime = new Date();
        const outputElement = createCommandRecord(
            command,
            response.message,
            response.status,
            commandTime,
            response.sensitive ?? false
        );
        appendToOutput(outputElement);
        addToCommandHistory(command);
    }, [createCommandRecord, addToCommandHistory]);

    const executeCommand = useCallback(async (parsedCommand: ParsedCommand) => {
        // Breakpoint 5: Start of command execution
        logger.debug('Executing command:', parsedCommand);
        const command = commandRegistry.getCommand(parsedCommand.command);
        if (command != null && context != null) {
            try {
                const response = await command.execute(context, parsedCommand);
                logger.debug('Command executed successfully:', response);
                processCommandOutput(parsedCommand, response);
                handleCommandExecuted(parsedCommand);
            } catch (error) {
                const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
                logger.error('Command execution failed:', error);
                processCommandOutput(parsedCommand, {
                    status: 500,
                    message: errorMessage
                });
                handleCommandExecuted(parsedCommand);
            }
        } else {
            // Breakpoint 6: Tutorial command handling
            const response = currentActivity.value === ActivityType.TUTORIAL ? `Tutorial attempt: ${parsedCommand.command}` : `Command not found: ${parsedCommand.command}`;
            logger.debug('Command not found:', { command: parsedCommand.command, activity: currentActivity.value });
            processCommandOutput(parsedCommand, {
                status: 404,
                message: response
            });
            handleCommandExecuted(parsedCommand);
        }
    }, [context, processCommandOutput, handleCommandExecuted, currentActivity]);

    const handleCommand = useCallback(async (parsedCommand: ParsedCommand) => {
        // Breakpoint 7: Start of command handling
        const initialActivity = currentActivity.value;
        logger.debug('Handling command:', {
            parsedCommand,
            activity: initialActivity,
            timestamp: new Date().toISOString()
        });

        setCommandTime(new Date());

        // Only check tutorial progress if we're explicitly in tutorial mode
        if (initialActivity === ActivityType.TUTORIAL) {
            // Breakpoint 8: Tutorial progress check
            logger.debug('Processing tutorial command:', {
                command: parsedCommand.command,
                activity: initialActivity
            });
            checkTutorialProgress(parsedCommand.command);

            // If activity changed during tutorial progress check, skip command execution
            if (currentActivity.value !== initialActivity) {
                logger.debug('Activity changed during tutorial progress - skipping command execution');
                return;
            }
        } else {
            logger.debug('Processing non-tutorial command:', {
                command: parsedCommand.command,
                activity: initialActivity
            });
        }

        await executeCommand(parsedCommand);

        logger.debug('Command handling complete:', {
            parsedCommand,
            activity: currentActivity.value,
            timestamp: new Date().toISOString()
        });
    }, [currentActivity.value, executeCommand, checkTutorialProgress]);

    return {
        output,
        resetOutput,
        commandHistory,
        addToCommandHistory,
        getCommandResponseHistory,
        handleCommand,
