import { LogKeys } from "../types/TerminalTypes";
import { type ParsedCommand } from "../types/Types";

export const commandTextToHTML = (text: string): string => {
  return text.replace(/\n/g, '<br/>');
};

export const loadCommandHistory = (): string[] => {
  const storedHistory = localStorage.getItem(LogKeys.commandHistory ?? '') ?? '[]';
  try {
    const parsedHistory = JSON.parse(storedHistory) as unknown;
    // If it's an array of strings, return it directly
    if (Array.isArray(parsedHistory) && parsedHistory.every((item): item is string => typeof item === 'string')) {
      return parsedHistory;
    }
    // If it's an array of ParsedCommand, convert to strings
    if (Array.isArray(parsedHistory) && parsedHistory.every((item): item is ParsedCommand =>
      typeof item === 'object' && item !== null && 'command' in item
    )) {
      return parsedHistory.map(parsedCommandToString);
    }
    // If parsing fails or is invalid, return an empty array
    return [];
  } catch {
    return [];
  }
}

export const saveCommandHistory = (commandHistory: string[]): void => {
  if (LogKeys.commandHistory != null)
    localStorage.setItem(LogKeys.commandHistory ?? '', JSON.stringify(commandHistory));
}

export function parsedCommandToString(cmd: ParsedCommand): string {
  const argsStr = cmd.args != null ? cmd.args.join(' ') : '';
  const switchesStr = cmd.switches == null ? '' : Object.entries(cmd.switches)
    .map(([key, value]) => {
      if (typeof value === 'boolean') {
        return value ? `-${key}` : '';
      }
      return `-${key} ${value}`;
    })
    .filter(s => s)
    .join(' ');

  return [cmd.command, argsStr, switchesStr]
    .filter(s => s)
    .join(' ')
    .trim();
}

function isSwitch(part: string): boolean {
  return part.substring(0, 1) === '--'  || (part.startsWith('-') && part.length === 2);
}

export const parseCommand = (input: string): ParsedCommand => {
  const parts = input.split(/\s+/).filter(Boolean); // Split by whitespace and remove empty strings
  const command = parts.shift() ?? ''; // The first element is the command
  const args: string[] = [];
  const switches: Record<string, boolean | string> = {};

  for (let i = 0; i < parts.length; i++) {
    const part: string | undefined = parts[i];
    if (part === undefined) continue;
    if (part == null) continue; // Skip if undefined (shouldn't happen due to filter)
    if (isSwitch(part)) {
      const switchAssignmentIndex = part.indexOf('=');
      if (switchAssignmentIndex > -1) {
        // It's a switch with an explicit value
        const switchName = part.substring(2, switchAssignmentIndex);
        const switchValue = part.substring(switchAssignmentIndex + 1);
        switches[switchName] = switchValue;
      } else {
        // It's a boolean switch or a switch with a value that's the next part
        const switchName = part.substring(0, 2) === '--' ? part.substring(2) : part.substring(1);
        // Look ahead to see if the next part is a value for this switch
        if (i + 1 < parts.length) {
          const nextPart = parts[i + 1];
          if (nextPart && !nextPart.startsWith('-')) {
            switches[switchName] = nextPart;
            i++; // Increment i to skip the value
          } else {
            switches[switchName] = true; // No value provided, treat it as a boolean switch
          }
        } else {
          switches[switchName] = true; // No value provided, treat it as a boolean switch
        }
      }
    } else {
      // It's an argument
      args.push(part);
    }
  }

  return {
    command,
    args,
    switches
  } as const;
}

export function createElement<T extends HTMLElement>(tagName: keyof HTMLElementTagNameMap, className?: string): T {
    const element = document.createElement(tagName) as T;
    if (className) {
        element.id = className;
        element.classList.add(className);
    }
    return element;
}

export function createHTMLElementFromHTML(htmlString: string): HTMLElement {
    const parser = new DOMParser();
    const doc = parser.parseFromString(htmlString, 'text/html');
    return doc.body.firstChild as HTMLElement;
}

// src/utils/navigationUtils.ts
import { ActivityType, type ParsedLocation } from 'src/types/Types';

// Parse location from either pathname or ?p= parameter
export function parseLocation(location: string = window.location.toString()): ParsedLocation {
  // First, check for ?p= parameter
  const urlParams = new URL(location);
  const pParam = urlParams.searchParams.get('p');

  if (pParam) {
    // Remove leading slash if present
    const cleanPath = pParam.startsWith('/') ? pParam.slice(1) : pParam;
    const [activityKey, phraseKey] = cleanPath.split('/');

    return {
      activityKey: activityKey == null ? ActivityType.NORMAL : parseActivityType(activityKey),
      contentKey: decodeURIComponent(phraseKey ?? ''),
      groupKey: urlParams.searchParams.get('group') ?? null
    };
  }

  // Fallback to pathname parsing
  const [, activityKey, phraseKey] = window.location.pathname.split('/');
  return {
    activityKey: activityKey == null ? ActivityType.NORMAL : parseActivityType(activityKey),
    contentKey: decodeURIComponent(phraseKey ?? ''),
    groupKey: urlParams.searchParams.get('group') ?? null
  };
}

// Helper function to parse activity type
export function parseActivityType(activityString: string): ActivityType {
  const normalizedActivity = (activityString ?? '').toUpperCase();
  return ActivityType[normalizedActivity as keyof typeof ActivityType] ?? ActivityType.NORMAL;
}

// Global navigation function that can be used outside of React components
export function navigate(options: ParsedLocation): void {
  const newActivity = options.activityKey ?? ActivityType.NORMAL;
  const newPhraseKey = options.contentKey != null ? options.contentKey.replace('\r', '_r') : null;
  const newGroupKey = options.groupKey ?? null;

  const encodedPhraseKey = newPhraseKey != null ? encodeURIComponent(newPhraseKey) : '';
  const queryString = newGroupKey != null ? `?group=${encodeURIComponent(newGroupKey)}` : '';

  const activityPath = newActivity === ActivityType.NORMAL ? '' : ActivityType[newActivity].toLowerCase();
  const path = `/${activityPath}${newPhraseKey !== null ? `/${encodedPhraseKey}` : ''}${queryString}`;

  // Use browser's history API directly
  window.history.pushState(null, '', path);

  // Dispatch a custom event to notify React router or other components about the navigation
  window.dispatchEvent(new CustomEvent('locationchange', { detail: { path } }));
}

interface TreeItem {
    path: string;
    type: string;
}

interface TreeState {
    expandedFolders: Set<string>;
}

export function formatTreeContent(items: TreeItem[], treeState: TreeState): string {
    const sortedItems = [...items].sort((a, b) => {
        // Directories come first
        if (a.type === 'tree' && b.type !== 'tree') return -1;
        if (a.type !== 'tree' && b.type === 'tree') return 1;
        return a.path.localeCompare(b.path);
    });

    const lines: string[] = ['Repository Files', ''];

    // First pass: collect all directories
    const directories = new Set<string>();
    sortedItems.forEach(item => {
        const parts = item.path.split('/');
        parts.pop(); // Remove the last part (file/dir name)
        let currentPath = '';
        parts.forEach(part => {
            currentPath = currentPath ? `${currentPath}/${part}` : part;
            directories.add(currentPath);
        });
    });

    // Convert directories to array and sort
    const sortedDirs = Array.from(directories).sort();

    // Helper to check if path should be visible based on parent expansion state
    const isVisible = (path: string): boolean => {
        if (!path.includes('/')) return true;
        const parentPath = path.split('/').slice(0, -1).join('/');
        return treeState.expandedFolders.has(parentPath) &&
               (parentPath.includes('/') ? isVisible(parentPath) : true);
    };

    // Build tree structure
    sortedDirs.forEach(dir => {
        if (isVisible(dir)) {
            const depth = dir.split('/').length;
            const indent = '  '.repeat(depth - 1);
            const name = dir.split('/').pop() ?? '';
            const isExpanded = treeState.expandedFolders.has(dir);
            const arrow = isExpanded ? '' : '';
            lines.push(`${indent}${arrow}  ${name}/`);
        }
    });

    // Add files under their directories
    sortedItems.forEach(item => {
        if (item.type !== 'tree' && isVisible(item.path)) {
            const parts = item.path.split('/');
            const fileName = parts.pop() ?? '';
            const depth = parts.length;
            const indent = '  '.repeat(depth);
            const prefix = parts.length > 0 ? '  ' : '';
            const icon = getFileIcon(fileName);
            lines.push(`${indent}${prefix}${icon} ${fileName}`);
        }
    });

    // Add navigation help
    lines.push('');
    lines.push('Navigation:');
    lines.push('j: move down');
    lines.push('k: move up');
    lines.push('Enter: open file or toggle folder');
    lines.push('e: close tree view');
    lines.push('');

    return lines.join('\n');
}

// Helper function to get the item at a specific line
export function getItemAtLine(
    items: TreeItem[],
    treeState: TreeState,
    lineNumber: number
): { path: string; type: string; isDirectory: boolean } | null {
    const content = formatTreeContent(items, treeState);
    const lines = content.split('\n');
    const line = lines[lineNumber - 1];

    if (!line || !line.match(/[]?\s*[]/u)) {
        return null;
    }

    // Extract path from the line
    const match = line.match(/[]?\s*[^\s]+\s+(.+?)\/?\s*$/u);
    if (!match) return null;

    const name = match[1];
    const indent = line.match(/^\s*/)?.[0].length || 0;
    const depth = Math.floor(indent / 2);

    // Reconstruct full path based on previous directory lines
    const pathParts: string[] = [];
    let currentDepth = 0;

    for (let i = lineNumber - 2; i >= 0 && currentDepth < depth; i--) {
        const prevLine = lines[i];
        const prevMatch = prevLine.match(/[]?\s*[^\s]+\s+(.+?)\/\s*$/u);
        if (prevMatch) {
            const prevIndent = prevLine.match(/^\s*/)?.[0].length || 0;
            const prevDepth = Math.floor(prevIndent / 2);
            if (prevDepth === currentDepth) {
                pathParts.unshift(prevMatch[1]);
                currentDepth++;
            }
        }
    }

    pathParts.push(name);
    const fullPath = pathParts.join('/');

    const isDirectory = line.includes('');
    return {
        path: fullPath,
        type: isDirectory ? 'tree' : 'blob',
        isDirectory
    };
}

// Helper function to get file icon based on extension
function getFileIcon(fileName: string): string {
    const ext = fileName.split('.').pop()?.toLowerCase();
    switch (ext) {
        case 'js':
        case 'jsx':
            return '';
        case 'ts':
        case 'tsx':
            return '';
        case 'json':
            return '';
        case 'md':
            return '';
        case 'html':
            return '';
        case 'css':
        case 'scss':
        case 'sass':
            return '';
        case 'py':
            return '';
        case 'rb':
            return '';
        case 'java':
            return '';
        case 'go':
            return '';
        case 'rs':
            return '';
        case 'php':
            return '';
        case 'sh':
        case 'bash':
            return '';
        case 'yml':
        case 'yaml':
            return '';
        case 'svg':
        case 'png':
        case 'jpg':
        case 'jpeg':
        case 'gif':
            return '';
        default:
            return '';
    }
}

import '@testing-library/jest-dom/vitest';
import { afterEach, vi } from 'vitest';
import { cleanup } from '@testing-library/react';
import { ImageData as CanvasImageData } from 'canvas';

// Extend CanvasImageData to include colorSpace property
interface ExtendedImageData extends CanvasImageData {
  colorSpace: PredefinedColorSpace;
}

// Create a type-safe ImageData constructor that matches both browser and Node.js environments
const createImageData = (
  width: number,
  height: number,
  data?: Uint8ClampedArray
): ExtendedImageData => {
  const imageData = new CanvasImageData(
    data || new Uint8ClampedArray(width * height * 4),
    width,
    height
  );
  return Object.assign(imageData, { colorSpace: 'srgb' as PredefinedColorSpace });
};

// Update ImageDataConstructor type to use ExtendedImageData
type ImageDataConstructorType = {
  new(sw: number, sh: number, settings?: ImageDataSettings): ExtendedImageData;
  new(data: Uint8ClampedArray, sw: number, sh?: number, settings?: ImageDataSettings): ExtendedImageData;
  prototype: ExtendedImageData;
};

// Create a type-safe ImageData constructor that matches the browser API
const ImageDataConstructor = {
  new: createImageData
} as unknown as ImageDataConstructorType;

global.ImageData = ImageDataConstructor;

// Polyfill DOMMatrix if not available
if (typeof DOMMatrix === 'undefined') {
  class DOMMatrixPolyfill {
    m11 = 1;
    m12 = 0;
    m13 = 0;
    m14 = 0;
    m21 = 0;
    m22 = 1;
    m23 = 0;
    m24 = 0;
    m31 = 0;
    m32 = 0;
    m33 = 1;
    m34 = 0;
    m41 = 0;
    m42 = 0;
    m43 = 0;
    m44 = 1;
    is2D = true;
    isIdentity = true;

    constructor(matrix?: string | number[]) {
      if (matrix) {
        const values = (typeof matrix === 'string'
          ? matrix.split(',').map(Number)
          : matrix).map(v => Number.isNaN(v) ? 0 : v);

        this.m11 = values[0] ?? 1;
        this.m12 = values[1] ?? 0;
        this.m13 = values[2] ?? 0;
        this.m14 = values[3] ?? 0;
        this.m21 = values[4] ?? 0;
        this.m22 = values[5] ?? 1;
        this.m23 = values[6] ?? 0;
        this.m24 = values[7] ?? 0;
        this.m31 = values[8] ?? 0;
        this.m32 = values[9] ?? 0;
        this.m33 = values[10] ?? 1;
        this.m34 = values[11] ?? 0;
        this.m41 = values[12] ?? 0;
        this.m42 = values[13] ?? 0;
        this.m43 = values[14] ?? 0;
        this.m44 = values[15] ?? 1;

        this.isIdentity = this.m11 === 1 && this.m12 === 0 && this.m13 === 0 && this.m14 === 0 &&
                         this.m21 === 0 && this.m22 === 1 && this.m23 === 0 && this.m24 === 0 &&
                         this.m31 === 0 && this.m32 === 0 && this.m33 === 1 && this.m34 === 0 &&
                         this.m41 === 0 && this.m42 === 0 && this.m43 === 0 && this.m44 === 1;
      }
    }

    static fromFloat32Array(array: Float32Array): DOMMatrix {
      return new DOMMatrixPolyfill([...array]) as unknown as DOMMatrix;
    }

    static fromFloat64Array(array: Float64Array): DOMMatrix {
      return new DOMMatrixPolyfill([...array]) as unknown as DOMMatrix;
    }

    static fromMatrix(other?: DOMMatrixInit): DOMMatrix {
      if (!other) {
        return new DOMMatrixPolyfill() as unknown as DOMMatrix;
      }

      const values = [
        other.m11 ?? 1, other.m12 ?? 0, other.m13 ?? 0, other.m14 ?? 0,
        other.m21 ?? 0, other.m22 ?? 1, other.m23 ?? 0, other.m24 ?? 0,
        other.m31 ?? 0, other.m32 ?? 0, other.m33 ?? 1, other.m34 ?? 0,
        other.m41 ?? 0, other.m42 ?? 0, other.m43 ?? 0, other.m44 ?? 1
      ];

      return new DOMMatrixPolyfill(values) as unknown as DOMMatrix;
    }
  }

  global.DOMMatrix = DOMMatrixPolyfill as unknown as typeof DOMMatrix;
}

