// src/commands/BypassCommand.ts

import { activitySignal , setBypassTutorial, bypassTutorialSignal } from 'src/signals/appSignals';

import { type ICommand, type ICommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { type ParsedCommand , ActivityType } from '../types/Types';
import { navigate } from '../utils/navigationUtils';

export const BypassCommand: ICommand = {
  name: 'bypass',
  description: 'Toggle tutorial bypass mode for testing',
  execute: async (
    _context: ICommandContext,
    _parsedCommand: ParsedCommand
  ): Promise<ICommandResponse> => {
    const newBypassState = !bypassTutorialSignal.value;
    setBypassTutorial(newBypassState);

    if(newBypassState){
      // Set activity route to normal
      activitySignal.value = ActivityType.NORMAL;
      navigate({
        activityKey: ActivityType.NORMAL,
        contentKey: null,
        groupKey: null
      });
    }

    return {
      status: 200,
      message: `Bypass mode ${newBypassState ? 'enabled' : 'disabled'}. ${newBypassState ? 'You are now in NORMAL mode.' : 'Tutorial mode will resume on next page load.'}`,
    };
  }
};

import { type ICommand, type ICommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { type ParsedCommand, ActivityType } from '../types/Types';
import { getRepoTree, listRecentRepos, unlinkGitHub, getGitHubDeviceCode, pollGitHubDeviceAuth } from '../utils/apiClient';
import { createLogger } from '../utils/Logger';

const logger = createLogger();

const POLL_INTERVAL = 5000; // 5 seconds
const MAX_POLL_TIME = 300000; // 5 minutes

async function sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
}

export const GitHubCommand: ICommand = {
    name: 'github',
    description: 'GitHub account and repository management',
    switches: {
        'h': 'Show help for GitHub command',
        'l': 'Link GitHub account',
        'u': 'Unlink GitHub account',
        'r': 'List recent repositories',
        't': 'Get repository tree (Usage: -t owner/repo [path] [sha])'
    },
    execute: async (
        context: ICommandContext,
        parsedCommand: ParsedCommand
    ): Promise<ICommandResponse> => {
        // Show help if requested
        if ('h' in parsedCommand.switches) {
            return {
                status: 200,
                message: `GitHub Command Usage:
                github -l : Link your GitHub account
                github -u : Unlink GitHub account
                github -r : List recent repositories
                github -t owner/repo [path] [sha] : Get repository tree
                github -h : Show this help message`
            };
        }

        try {
            if ('l' in parsedCommand.switches) {
                // Get device code from GitHub
                const deviceCodeResponse = await getGitHubDeviceCode(context.auth);
                if (deviceCodeResponse.status !== 200 || !deviceCodeResponse.data) {
                    return {
                        status: deviceCodeResponse.status,
                        message: deviceCodeResponse.error || 'Failed to get device code'
                    };
                }

                const { verification_uri, user_code, device_code, interval } = deviceCodeResponse.data;

                // Copy code to clipboard
                await navigator.clipboard.writeText(user_code);

                // Open browser to verification URL
                window.open(verification_uri, '_blank');

                // Log authentication steps
                logger.info(`Opening browser for GitHub authentication...`);
                logger.info(`Device code copied to clipboard!`, user_code);
                logger.info(`Waiting for authentication...`);

                // Poll for completion
                const startTime = Date.now();
                while (Date.now() - startTime < MAX_POLL_TIME) {
                    const pollResponse = await pollGitHubDeviceAuth(context.auth, device_code);

                    if (pollResponse.status === 200 && pollResponse.data?.status === 'complete') {
                        return {
                            status: 200,
                            message: 'Successfully linked GitHub account!'
                        };
                    }

                    if (pollResponse.status !== 202) {
                        return {
                            status: pollResponse.status,
                            message: pollResponse.error || 'Failed to check authorization status'
                        };
                    }

                    await sleep(interval * 1000 || POLL_INTERVAL);
                }

                return {
                    status: 408,
                    message: 'Authentication timed out. Please try again.'
                };
            }

            if ('u' in parsedCommand.switches) {
                const response = await unlinkGitHub(context.auth);
                if (response.status === 200) {
                    // Clear GitHub-related items from localStorage
                    localStorage.removeItem('githubUsername');
                    localStorage.removeItem('current_github_repo');
                    localStorage.removeItem('github_tree_items');
                    sessionStorage.removeItem('github_auth_state');

                    return {
                        status: 200,
                        message: 'GitHub account unlinked. Use "github -l" to link again with new permissions.'
                    };
                }

                return {
                    status: response.status,
                    message: response.error || 'Failed to unlink GitHub account.'
                };
            }

            if ('r' in parsedCommand.switches) {
                const response = await listRecentRepos(context.auth);

                if (response.status === 200 && response.data) {
                    const repoList = response.data.map(repo =>
                        `${repo.name}: ${repo.description || 'No description'}`
                    ).join('<br />');

                    return {
                        status: 200,
                        message: response.data.length > 0
                            ? `Recent Repositories:<br />${repoList}`
                            : 'No recent repositories found.'
                    };
                }

                return {
                    status: response.status,
                    message: response.error || 'Failed to retrieve repositories.'
                };
            }

            if ('t' in parsedCommand.switches) {
                const repoArg = parsedCommand.switches.t === true ? parsedCommand.args[0] : parsedCommand.switches.t;
                if (!repoArg) {
                    return {
                        status: 400,
                        message: 'Repository parameter required. Usage: github -t owner/repo [path] [sha]'
                    };
                }

                const path = parsedCommand.args[1] || '';
                const sha = parsedCommand.args[2] || '';

                logger.info('Fetching tree for repo:', repoArg);
                const response = await getRepoTree(context.auth, repoArg, path, sha);
                logger.info('Tree response:', response);

                if (response.status === 200 && response.data) {
                    // Store current repository for file fetching
                    localStorage.setItem('current_github_repo', repoArg);

                    // Store tree items in localStorage
                    localStorage.setItem('github_tree_items', JSON.stringify(response.data));

                    // Switch to tree view mode
                    logger.info('Switching to TREE mode');
                    context.updateLocation({
                        activityKey: ActivityType.TREE,
                        contentKey: null,
                        groupKey: null
                    });

                    return {
                        status: 200,
                        message: 'Repository tree loaded. Use j/k to navigate, Enter to select a file, e to close.'
                    };
                }

                return {
                    status: response.status,
                    message: response.error || 'Failed to retrieve repository tree.'
                };
            }

            return {
                status: 400,
                message: 'Invalid command. Use -h to show command help info',
            };
        } catch (error) {
            logger.error('GitHub command error:', error);
            return {
                status: 501,
                message: 'Failed to process GitHub command',
            };
        }
    }
}

import ReactDOMServer from 'react-dom/server';

import { Chord } from '../components/Chord';
import { type ICommand, type ICommandResponse , type ICommandContext } from '../contexts/CommandContext';
import { type ParsedCommand } from '../types/Types';

export const HelpCommand: ICommand = {
  name: 'help',
  description: 'Display help information',
  execute: async (
    context: ICommandContext,
    parsedCommand: ParsedCommand,
  ): Promise<ICommandResponse> => {
    if (
      parsedCommand.command === 'help'
      || parsedCommand.command === '411'
    ) {
      const commandChords = [
        'DELETE (Backspace)',
        'Return (ENTER)',
        'UpArrow',
        'LeftArrow',
        'DownArrow',
        'RightArrow',
        'ESCAPE',
      ].map(c => {
        return <Chord key={c} displayChar={c} />;
      });
      const commandChordsHtml = commandChords.map(element => {
        return ReactDOMServer.renderToStaticMarkup(element);
      }).join('');
      const response = "<div class='chord-display-container'>" + commandChordsHtml + "</div>";
      return { status: 200, message: response };
    }
    return { status: 404, message: "Help command not recognized" };
  }
};

import { type ICommand, type ICommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { Phrases, type GamePhrase, type ParsedCommand } from '../types/Types';

export const ListPhrasesCommand: ICommand = {
  name: 'ls',
  description: 'List files',
  switches: {
    'all': 'List all phrases',
    'random': 'List a random phrase',
    'easy': 'List only easy phrases',
  },
  execute: async (
    context: ICommandContext,
    _parsedCommand: ParsedCommand
  ): Promise<ICommandResponse> => {
    if (!context) {
      return { status: 404, message: 'No command context available.' };
    }

    const phrases: string = Phrases
      .map((phrase: GamePhrase) => phrase.key)
      .join('\n');

    return {
      status: 200,
      message: phrases
    };
  }
};

// src/commands/SignUpCommand.ts
import { tempUserNameSignal, setTempEmail, tempEmailSignal, setTempUserName, tempPasswordSignal, isInSignUpProcessSignal, setIsInSignUpProcess } from 'src/signals/appSignals';

import { type ICommand, type ICommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { type ParsedCommand } from '../types/Types';

export const SignUpCommand: ICommand = {
    name: 'signup',
    description: 'Create a new account',
    execute: async (
        context: ICommandContext, 
        _parsedCommand: ParsedCommand
    ): Promise<ICommandResponse> => {
        const { auth } = context;

        if (_parsedCommand.args.length === 2) {
            // Start signup process
            setIsInSignUpProcess(true);
            setTempUserName(_parsedCommand.args[0]);
            setTempEmail(_parsedCommand.args[1])
            return { status: 200, message: 'Enter password:' };
        } else if (_parsedCommand.args.length === 3 && isInSignUpProcessSignal.value) {
            // Complete signup process
            const username = tempUserNameSignal.value;
            const password = tempPasswordSignal.value;
            const email = tempEmailSignal.value;

            try {
                const result = await auth.signup({ username, password, email });
                setIsInSignUpProcess(false);

                if (result.status === 200) {
                    return {
                        status: 200,
                        message: 'Account created successfully! Please check your email for verification.'
                    };
                } else {
                    return {
                        status: result.status,
                        message: `Signup failed: ${result.message}`
                    };
                }
            } catch (error) {
                setIsInSignUpProcess(false);
                return {
                    status: 500,
                    message: `Signup error: ${error instanceof Error ? error.message : 'Unknown error'}`
                };
            }
        } else {
            return { status: 400, message: 'Usage: signup <username> <email>' };
        }
    }
};

import React from 'react';

import ReactDOMServer from 'react-dom/server';

import { Chord } from '../components/Chord';
import { type ICommand, type ICommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { type ParsedCommand } from '../types/Types';

export const SpecialCommand: ICommand = {
  name: 'special',
  description: 'Display special characters',
  execute: async (
    _context: ICommandContext,
    _parsedCommand: ParsedCommand,
  ): Promise<ICommandResponse> => {
    const specialChars = ['~', '`', '!', '@', '#', '$', '%', '^', '&', '*', '(', ')', '-', '_', '+', '=', '{', '[', '}', ']', '|', '\\', ':', ';', '"', "'", '<', '>', ',', '.', '?', '/'];
    const chordElements = specialChars.map(char => (
      <Chord key={char} displayChar={char} />
    ));
    const chordsHtml = chordElements.map(element =>
      ReactDOMServer.renderToStaticMarkup(element)
    ).join('');

    return {
      status: 200,
      message: `<div class='chord-display-container'>${chordsHtml}</div>`
    };
  }
};

import { describe, it, expect, vi, beforeEach, type MockedFunction } from 'vitest';

import * as appSignals from 'src/signals/appSignals';
import {
  type ICommandContext,
  type ICommandResponse,
  type ParsedCommand,
  type IAuthProps,
  type IHandTermWrapperMethods,
  type MyResponse,
  type AuthResponse
} from 'src/types/HandTerm';

import { LoginCommand } from '../LoginCommand';

// Type-safe mock of signals
interface SignalMock { value: boolean }
type SetSignalFn = (value: boolean) => void;

// Mock the signals
vi.mock('src/signals/appSignals', () => ({
  isInLoginProcessSignal: { value: false } as SignalMock,
  setIsInLoginProcess: vi.fn() as unknown as SetSignalFn,
  setTempUserName: vi.fn(),
  tempUserNameSignal: { value: '' } as { value: string }
}));

describe('LoginCommand', () => {
  // Create a more complete mock of ICommandContext
  const createMockContext = (overrides: Partial<ICommandContext> = {}): ICommandContext => ({
    executeCommand: vi.fn(),
    commandHistory: [],
    addToCommandHistory: vi.fn(),
    output: [],
    appendToOutput: vi.fn(),
    handTermRef: {
      current: {
        writeOutput: vi.fn(),
        prompt: vi.fn(),
        saveCommandResponseHistory: vi.fn(),
        focusTerminal: vi.fn(),
        handleCharacter: vi.fn(),
        refreshComponent: vi.fn(),
        setHeroSummersaultAction: vi.fn(),
        setEditMode: vi.fn(),
        handleEditSave: vi.fn()
      } as IHandTermWrapperMethods
    },
    auth: {
      login: vi.fn(),
      signup: vi.fn(),
      verify: vi.fn(),
      refreshToken: vi.fn(),
      validateAndRefreshToken: vi.fn(),
      isLoggedIn: false,
      isLoading: false,
      isError: false,
      error: null,
      isPending: false
    } as IAuthProps,
    updateLocation: vi.fn(),
    ...overrides
  });

  beforeEach(() => {
    vi.clearAllMocks();
    // Reset signal values with type-safe approach
    (appSignals.isInLoginProcessSignal as SignalMock).value = false;
  });

  it('should initiate login process with username', async () => {
    const mockContext = createMockContext();
    const parsedCommand: ParsedCommand = {
      command: 'login',
      args: ['testuser'],
      switches: {}
    };

    const response: ICommandResponse = await LoginCommand.execute(mockContext, parsedCommand);

    expect(appSignals.setIsInLoginProcess).toHaveBeenCalledWith(true);
    expect(appSignals.setTempUserName).toHaveBeenCalledWith('testuser');
    expect(response.status).toBe(200);
    expect(response.message).toBe('Enter password:');
  });

  it('should complete login process successfully', async () => {
    const mockContext = createMockContext();
    // Type-safe mock of login method with full MyResponse structure
    const mockLogin = mockContext.auth.login as MockedFunction<typeof mockContext.auth.login>;
    const successResponse: MyResponse<AuthResponse> = {
      status: 200,
      data: {
        AccessToken: 'mock-access-token',
        RefreshToken: 'mock-refresh-token',
        IdToken: 'mock-id-token',
        ExpiresIn: '3600'
      },
      message: 'Login successful',
      error: []
    };
    mockLogin.mockResolvedValue(successResponse);

    // Simulate being in login process
    (appSignals.isInLoginProcessSignal as SignalMock).value = true;

    const parsedCommand: ParsedCommand = {
      command: 'login',
      args: ['testuser', 'password123'],
      switches: {}
    };

    const response: ICommandResponse = await LoginCommand.execute(mockContext, parsedCommand);

    expect(mockLogin).toHaveBeenCalledWith('testuser', 'password123');
    expect(appSignals.setIsInLoginProcess).toHaveBeenCalledWith(false);
    expect(response.status).toBe(200);
    expect(response.message).toBe('Login successful!');
    expect(response.sensitive).toBe(true);
  });

  it('should handle login failure', async () => {
    const mockContext = createMockContext();
    // Type-safe mock of login method with error response
    const mockLogin = mockContext.auth.login as MockedFunction<typeof mockContext.auth.login>;
    const errorResponse: MyResponse<AuthResponse> = {
      status: 401,
      message: 'Login error: An unknown error occurred',
      error: ['Authentication failed'],
      data: undefined
    };
    mockLogin.mockRejectedValue(errorResponse);

    // Simulate being in login process
    (appSignals.isInLoginProcessSignal as SignalMock).value = true;

    const parsedCommand: ParsedCommand = {
      command: 'login',
      args: ['testuser', 'wrongpassword'],
      switches: {}
    };

    const response: ICommandResponse = await LoginCommand.execute(mockContext, parsedCommand);

    expect(mockLogin).toHaveBeenCalledWith('testuser', 'wrongpassword');
    expect(appSignals.setIsInLoginProcess).toHaveBeenCalledWith(false);
    expect(response.status).toBe(500);
    expect(response.message).toBe('Login error: An unknown error occurred');
    expect(response.sensitive).toBe(true);
  });

  it('should reject invalid command format', async () => {
    const mockContext = createMockContext();
    const parsedCommand: ParsedCommand = {
      command: 'login',
      args: [],
      switches: {}
    };

    const response: ICommandResponse = await LoginCommand.execute(mockContext, parsedCommand);

    expect(response.status).toBe(400);
    expect(response.message).toBe('Usage: login <username>');
  });
});

import { type ICommand, type ICommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { LogKeys } from '../types/TerminalTypes';
import { type ParsedCommand , type OutputElement } from '../types/Types';

interface HandTermProps {
    auth: {
        saveLog: (key: string, content: string, format: string) => Promise<boolean>;
    };
    prompt: () => void;
}

interface HandTermCurrent {
    current?: {
        props: HandTermProps;
        prompt: () => void;
    } | null;
}

export const archiveCommand: ICommand = {
    name: 'archive',
    description: 'Archive the current command history',
    execute: async (
        context: ICommandContext,
        parsedCommand: ParsedCommand,
    ): Promise<ICommandResponse> => {
        const archiveNext = async (index: number): Promise<ICommandResponse> => {
            if (index >= localStorage.length) return { status: 200, message: 'Command history archived.' }; // Stop if we've processed all items

            const key = localStorage.key(index);
            if (!key) {
                await archiveNext(index + 1); // Skip and move to the next item
                return {
                    status: 500,
                    message: 'Stopping archive due to localStorage key not found.'
                };
            }

            if (key.startsWith(LogKeys.Command + '_') || key.startsWith(LogKeys.CharTime + '_')) {
                if (key.includes('_archive_')) {
                    localStorage.removeItem(key);
                } else {
                    const logKey = key.substring(0, key.indexOf('_'));
                    const content = localStorage.getItem(key);
                    if (content) {
                        try {
                            const _handTerm: HandTermCurrent = window._handTerm || { current: null };

                            if (!_handTerm.current?.props.auth) {
                                const outputElement: OutputElement = {
                                    command: parsedCommand,
                                    response: 'Authentication not available.',
                                    status: 500,
                                    commandTime: new Date()
                                };
                                context.appendToOutput(outputElement);
                                return {
                                    status: 500,
                                    message: 'Authentication not available.'
                                };
                            }

                            const result = await _handTerm.current.props.auth.saveLog(key, content, 'json');
                            if (!result) {
                                // If saveLog returns false, stop the archiving process
                                const outputElement: OutputElement = {
                                    command: parsedCommand,
                                    response: 'Stopping archive due to saveLog returning false.',
                                    status: 500,
                                    commandTime: new Date()
                                };
                                context.appendToOutput(outputElement);
                                return {
                                    status: 500,
                                    message: 'Stopping archive due to saveLog returning false.'
                                };
                            }
                            localStorage.setItem(key.replace(logKey, logKey + '_archive'), content);
                            localStorage.removeItem(key);
                        } catch (e) {
                            const errorMessage = e instanceof Error ? e.message : 'An unknown error occurred';
                            const outputElement: OutputElement = {
                                command: parsedCommand,
                                response: `Archive error: ${errorMessage}`,
                                status: 500,
                                commandTime: new Date()
                            };
                            context.appendToOutput(outputElement);
                            return {
                                status: 500,
                                message: `Archive error: ${errorMessage}`
                            };
                        }
                    }
                }
            }

            // Use setTimeout to avoid blocking the main thread
            // and process the next item in the next event loop tick
            return new Promise((resolve) => {
                setTimeout(async () => {
                    const result = await archiveNext(index + 1);
                    resolve(result);
                }, 300);
            });
        };

        // Ensure _handTerm is defined in the global scope
        if (typeof window._handTerm === 'undefined') {
            window._handTerm = { current: null };
        }

        const result = await archiveNext(0); // Start processing from the first item
        window._handTerm.current?.prompt();
        return result;
    },
}

// Extend the global Window interface to include _handTerm
declare global {
    interface Window {
        _handTerm: HandTermCurrent;
    }
}

