// useBaseCharacter.tsx

import type React from 'react';
import { useEffect, useRef, useState, useCallback, useMemo } from 'react';

import { type Sprite } from './sprites/Sprite';
import { SpriteManager } from './sprites/SpriteManager';
import { type Action, type ActionType } from './types/ActionTypes';
import { type SpritePosition } from './types/Position';
import { type SpriteAnimation } from './types/SpriteTypes';

interface BaseCharacterProps {
  currentActionType: ActionType;
  actions: Record<ActionType, Action>;
  name: string;
  scale: number;
  xOffset: number;
  positionRef: React.RefObject<SpritePosition>;
}

export const useBaseCharacter = (props: BaseCharacterProps) => {
  const [, setSprite] = useState<Sprite | null>(null);
  const spriteManager = useMemo(() => new SpriteManager(), []);
  const frameIndexRef = useRef<number>(0);
  const currentActionRef = useRef<ActionType>(props.currentActionType);

  // Initialize spritesRef with undefined values for all action types
  const spritesRef = useRef<Record<ActionType, Sprite | undefined>>(
    Object.keys(props.actions).reduce((acc, key) => ({
      ...acc,
      [key]: undefined
    }), {} as Record<ActionType, Sprite | undefined>)
  );

  const { currentActionType, actions } = props;

  const loadSprite = useCallback(async (actionKey: ActionType, animationData: SpriteAnimation) => {
    const loadedSprite = await spriteManager.loadSprite(animationData);
    if (loadedSprite) {
      spritesRef.current[actionKey] = loadedSprite;
      if (actionKey === currentActionRef.current) {
        setSprite(loadedSprite);
      }
    }
  }, [spriteManager]);

  const setCurrentActionType = useCallback((newActionType: ActionType) => {
    if (currentActionRef.current === newActionType) return;

    // Update both the ref and the prop
    currentActionRef.current = newActionType;
    frameIndexRef.current = 0;

    const sprite = spritesRef.current[newActionType];
    if (sprite) {
      setSprite(sprite);
    } else {
      // Try to load the sprite if it's missing
      loadSprite(newActionType, actions[newActionType].animation);
    }
  }, [actions, loadSprite]);

  const draw = (
    context: CanvasRenderingContext2D,
    positionRef: React.RefObject<SpritePosition>,
    scale: number | null
  ): number => {
    const sprite = spritesRef.current[currentActionRef.current];
    const action = actions[currentActionRef.current];
    const newX = (positionRef?.current?.leftX ?? 0) + props.xOffset + action.dx;

    if (sprite) {
      const nextFrameIndex = (frameIndexRef.current + 1) % sprite.frameCount;
      frameIndexRef.current = nextFrameIndex;

      sprite.draw(
        context,
        frameIndexRef.current,
        newX,
        positionRef?.current?.topY ?? 0,
        scale ?? props.scale
      );
    }
    return action.dx;
  }

  // Load all sprites on mount
  useEffect(() => {
    Object.entries(actions).forEach(([actionKey, actionData]) => {
      loadSprite(actionKey as ActionType, actionData.animation);
    });

    return () => {
      // Clear sprite references on unmount
      spritesRef.current = Object.keys(actions).reduce((acc, key) => ({
        ...acc,
        [key]: undefined
      }), {} as Record<ActionType, Sprite | undefined>);
      setSprite(null);
    };
  }, [actions, loadSprite]);

  // Handle prop changes for currentActionType
  useEffect(() => {
    if (currentActionType !== currentActionRef.current) {
      setCurrentActionType(currentActionType);
    }
  }, [currentActionType, setCurrentActionType]);

  // Handle initial sprite loading for current action
  useEffect(() => {
    const currentSprite = spritesRef.current[currentActionRef.current];
    if (!currentSprite) {
      loadSprite(currentActionRef.current, actions[currentActionRef.current].animation);
    }
  }, [actions, loadSprite]);

  return {
    draw,
    setCurrentActionType  // Export this so parent components can trigger action changes
  };
};

import { useCallback } from 'react';

import axios, { type AxiosError } from 'axios';

import ENDPOINTS from '../shared/endpoints.json';

import { type IAuthProps } from './useAuth';

interface APIResponse<T> {
    status: number;
    data?: T;
    error?: string;
}

interface FileContentResponse {
    content: string;
    encoding?: string;
    sha?: string;
}

interface TreeItemResponse {
    path: string;
    type: string;
    sha?: string;
    size?: number;
}

interface RepoResponse {
    name: string;
    description?: string;
    private: boolean;
    default_branch: string;
}

interface APIError {
    status: number;
    message: string;
}

export function useAPI(auth: IAuthProps) {
    const makeAuthenticatedRequest = useCallback(async <T>(
        endpoint: string,
        params?: Record<string, string>,
        method: 'GET' | 'POST' = 'GET'
    ): Promise<APIResponse<T>> => {
        try {
            const authResponse = await auth.validateAndRefreshToken();
            if (!authResponse || authResponse.status !== 200 || !authResponse.data) {
                return {
                    status: 401,
                    error: 'Authentication failed'
                };
            }

            const response = await axios({
                method,
                url: `${ENDPOINTS.api.BaseUrl}${endpoint}`,
                headers: {
                    'Authorization': `Bearer ${authResponse.data.AccessToken}`,
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                params
            });

            return {
                status: response.status,
                data: response.data
            };
        } catch (error) {
            const apiError = error as AxiosError<APIError>;

            // Log error without using console.error
            const errorMessage = apiError.response?.data?.message ||
                                 apiError.message ||
                                 'Request failed';

            if (typeof window !== 'undefined' && window.console && window.console.error) {
                window.console.error('API request failed:', errorMessage);
            }

            return {
                status: apiError.response?.status || 500,
                error: errorMessage
            };
        }
    }, [auth]);

    const getRepoTree = useCallback(async (repo: string, path?: string, sha?: string) => {
        return makeAuthenticatedRequest<TreeItemResponse[]>(ENDPOINTS.api.GetRepoTree, {
            repo,
            ...(path && { path }),
            ...(sha && { sha })
        });
    }, [makeAuthenticatedRequest]);

    const getFileContent = useCallback(async (repo: string, path: string) => {
        return makeAuthenticatedRequest<FileContentResponse>('/github/file', {
            repo,
            path
        });
    }, [makeAuthenticatedRequest]);

    const listRecentRepos = useCallback(async () => {
        return makeAuthenticatedRequest<RepoResponse[]>(ENDPOINTS.api.ListRecentRepos);
    }, [makeAuthenticatedRequest]);

    return {
        getRepoTree,
        getFileContent,
        listRecentRepos
    };
}

import { ActionType } from '../game/types/ActionTypes';
import { ParsedCommand } from '../types/Types';

export declare const _ActivityType: null;

export type IActivityMediatorReturn = {
  isInGameMode: boolean;
  isInTutorial: boolean;
  isInEdit: boolean;
  isInNormal: boolean;
  heroAction: ActionType;
  zombie4Action: ActionType;
  handleCommandExecuted: (parsedCommand: ParsedCommand) => boolean;
  setHeroAction: React.Dispatch<React.SetStateAction<ActionType>>,
  setZombie4Action: React.Dispatch<React.SetStateAction<ActionType>>;
  checkTutorialProgress: (command: string | null) => void;
  checkGameProgress: (successPhrase: { key: string; value: string; tutorialGroup?: string }) => void;
}

import { useState, useCallback, useEffect, useRef } from 'react';

import { useComputed } from '@preact/signals-react';

import { activitySignal, setNotification, bypassTutorialSignal } from 'src/signals/appSignals'
import {
    getIncompletePhrasesByTutorialGroup, initializeGame,
    setCompletedGamePhrase,
    getNextGamePhrase,
    setGamePhrase,
} from 'src/signals/gameSignals';
import {
    setNextTutorial, resetCompletedTutorials,
    tutorialSignal, getNextTutorial, setCompletedTutorial
} from 'src/signals/tutorialSignals';
import { createLogger } from 'src/utils/Logger';
import { navigate, parseLocation } from 'src/utils/navigationUtils';

import { type ActionType } from '../game/types/ActionTypes';
import { ActivityType, type ParsedCommand, type GamePhrase, type Tutorial } from '../types/Types';
import GamePhrases from '../utils/GamePhrases';

import { useTutorial } from './useTutorials';


const logger = createLogger({ prefix: 'ActivityMediator' });

export function useActivityMediator() {
    const [heroAction, setHeroAction] = useState<ActionType>('Idle');
    const [zombie4Action, setZombie4Action] = useState<ActionType>('Walk');
    const {
        getIncompleteTutorialsInGroup,
        canUnlockTutorial
    } = useTutorial();
    const activity = useComputed(() => activitySignal.value).value;
    const bypassTutorial = useComputed(() => bypassTutorialSignal.value);
    const currentTutorialRef = useRef<Tutorial | null>(null);

    const transitionToGame = useCallback((contentKey?: string | null, groupKey?: string | null) => {
        // First initialize game if group key is provided
        if (groupKey) {
            initializeGame(groupKey);
        } else {
            initializeGame();
        }
        // Then update activity and navigate
        activitySignal.value = ActivityType.GAME;
        navigate({
            activityKey: ActivityType.GAME,
            contentKey: contentKey ?? null,
            groupKey: groupKey ?? null
        });
    }, []);

    const decideActivityChange = useCallback((commandActivity: ActivityType | null = null): ActivityType => {
        logger.debug('Deciding activity change:', {
            commandActivity,
            currentActivity: activity,
            bypassTutorial: bypassTutorial.value,
            currentTutorial: tutorialSignal.value
        });

        const bypassTutorialValue = bypassTutorial.value;
        if (bypassTutorialValue) {
            return ActivityType.NORMAL;
        }
        if (tutorialSignal.value && !tutorialSignal.value?.tutorialGroup && activity !== ActivityType.GAME) {
            return ActivityType.TUTORIAL;
        }

        if (currentTutorialRef.current && currentTutorialRef.current?.tutorialGroup && activity !== ActivityType.GAME) {
            return ActivityType.GAME;
        }
        if (getNextTutorial()) commandActivity = ActivityType.TUTORIAL;

        return commandActivity ?? ActivityType.NORMAL;
    }, [activity, bypassTutorial]);

    const checkGameProgress = useCallback((successPhrase: GamePhrase) => {
        logger.debug('Checking game progress:', successPhrase);
        const groupKey = parseLocation().groupKey ?? '';
        setCompletedGamePhrase(successPhrase.key);
        if (groupKey) {
            const nextPhraseInGroup = getIncompletePhrasesByTutorialGroup(groupKey)[0];
            if (nextPhraseInGroup) {
                setGamePhrase(getNextGamePhrase());
                transitionToGame(nextPhraseInGroup.key, nextPhraseInGroup.tutorialGroup);
                return;
            }
            const incompleteTutorialInGroup = getIncompleteTutorialsInGroup(groupKey);
            incompleteTutorialInGroup.forEach(itig => {
                setCompletedTutorial(itig.phrase);
            });
            const nextTutorial = getNextTutorial();
            if (nextTutorial) {
                const resultActivity = decideActivityChange(ActivityType.TUTORIAL);
                navigate({
                    activityKey: resultActivity,
                    contentKey: nextTutorial.phrase ?? '',
                    groupKey: nextTutorial.tutorialGroup ?? ''
                })
                return;
            }
        }

        const nextGamePhrase = getNextGamePhrase();
        if (nextGamePhrase) {
            setGamePhrase(nextGamePhrase);
            transitionToGame(nextGamePhrase.key, nextGamePhrase.tutorialGroup);
            return;
        }
        activitySignal.value = ActivityType.NORMAL;
        navigate({ activityKey: ActivityType.NORMAL })
    }, [
        getIncompleteTutorialsInGroup,
        transitionToGame
    ]);

    const checkTutorialProgress = useCallback((command: string | null) => {
        logger.debug('Checking tutorial progress:', {
            command,
            currentTutorial: tutorialSignal.value,
            activity: activitySignal.value,
            location: parseLocation()
        });

        currentTutorialRef.current = getNextTutorial();
        if (!currentTutorialRef.current) {
            logger.debug('No current tutorial found');
            return;
        }

        const groupKey = parseLocation().groupKey ?? '';
        // Normalize command for Enter key
        command = command === '' ? '\r' : command;
        if (command) {
            logger.debug('Checking if can unlock tutorial:', {
                command,
                currentPhrase: currentTutorialRef.current.phrase,
                charCodesCommand: [...command].map(c => c.charCodeAt(0)),
                charCodesPhrase: [...currentTutorialRef.current.phrase].map(c => c.charCodeAt(0))
            });

            if (canUnlockTutorial(command)) {
                logger.debug('Tutorial unlocked:', command);
                if (groupKey) {
                    const incompletePhrasesInGroup = getIncompletePhrasesByTutorialGroup(groupKey)[0];
                    if (incompletePhrasesInGroup) {
                        transitionToGame(incompletePhrasesInGroup.key, incompletePhrasesInGroup.tutorialGroup);
                    }
                    return;
                }
                setCompletedTutorial(currentTutorialRef.current.phrase);
            } else {
                logger.debug('Tutorial not unlocked:', {
                    expected: currentTutorialRef.current.phrase,
                    received: command
                });
                setNotification(
                    `Tutorial ${tutorialSignal.value?.phrase} not unlocked with ${command}`
                )
                return;
            }
        }

        const nextTutorial = getNextTutorial();
        logger.debug('Next tutorial:', nextTutorial);
        if (nextTutorial?.phrase) {
            const resultActivity = decideActivityChange(ActivityType.TUTORIAL);
            setNextTutorial(nextTutorial);
            navigate({
                activityKey: resultActivity,
                contentKey: nextTutorial.phrase,
                groupKey: nextTutorial.tutorialGroup
            })
            return;
        }
        const nextGamePhrase = getNextGamePhrase();
        if (nextGamePhrase) {
            transitionToGame(nextGamePhrase?.key, groupKey);
        }
        return;
    }, [
        canUnlockTutorial,
        transitionToGame
    ]);

    const handleCommandExecuted = useCallback((parsedCommand: ParsedCommand): boolean => {
        logger.debug('Handling command:', parsedCommand);
        let result = false;
        if (parseLocation().activityKey === ActivityType.TUTORIAL) {
            checkTutorialProgress(parsedCommand.command);
        }
        else if (parseLocation().activityKey === ActivityType.GAME && parseLocation().contentKey) {
            const gamePhrase = GamePhrases.getGamePhraseByKey(parseLocation().contentKey || '')
            if (gamePhrase) checkGameProgress(gamePhrase);
        }
        switch (parsedCommand.command) {
            case 'play': {
                const nextGamePhrase = getNextGamePhrase();
                transitionToGame(nextGamePhrase?.key);
                result = true;
                break;
            }
            case 'tut': {
                if ('r' in parsedCommand.switches) {
                    resetCompletedTutorials();
                }
                const nextTutorial = getNextTutorial();
                navigate({
                    activityKey: ActivityType.TUTORIAL,
                    contentKey: nextTutorial?.phrase,
                    groupKey: nextTutorial?.tutorialGroup
                })
                result = true;
                break;
            }
            default:
                result = false;
        }

        return result;
    }, [checkGameProgress, checkTutorialProgress, transitionToGame]);

    useEffect(() => {
        const resultActivity = decideActivityChange(null);
        if (resultActivity === ActivityType.TUTORIAL) {
            checkTutorialProgress(null);
        }
    }, [decideActivityChange, checkTutorialProgress]);

    return {
        isInGameMode: activity === ActivityType.GAME,
        isInTutorial: activity === ActivityType.TUTORIAL,
        isInEdit: activity === ActivityType.EDIT,
        isInNormal: activity === ActivityType.NORMAL,
        checkTutorialProgress,
        heroAction,
        zombie4Action,
        handleCommandExecuted,
        setHeroAction,
        setZombie4Action,
        checkGameProgress,
    };
}

// src/hooks/useAuth.ts
import { useEffect, useCallback } from 'react';

import axios from 'axios';

import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';

import endpoints from 'src/shared/endpoints.json';
import { createLogger, LogLevel } from 'src/utils/Logger';

import {
  setIsLoggedIn,
  setUserName,
  setIsInLoginProcess,
  isLoggedInSignal
} from '../signals/appSignals';
import { type MyResponse } from '../types/Types';

const logger = createLogger({
  prefix: 'Auth',
  level: LogLevel.ERROR
});

const API_URL = endpoints.api.BaseUrl;

interface LoginCredentials {
  username: string;
  password: string;
}

interface SignUpCredentials extends LoginCredentials {
  email: string;
}

interface AuthResponse {
  accessToken: string;
  refreshToken: string;
  idToken: string;
  expiresAt: string;
  expiresIn: string;
  githubUsername?: string | undefined; // Explicitly define githubUsername as string | undefined
}

export interface IAuthProps {
  login: (username: string, password: string) => Promise<MyResponse<AuthResponse>>;
  signup: (credentials: SignUpCredentials) => Promise<MyResponse<unknown>>;
  verify: (username: string, code: string) => Promise<unknown>;
  refreshToken: () => Promise<MyResponse<AuthResponse>>;
  validateAndRefreshToken: () => Promise<MyResponse<AuthResponse>>;
  isLoggedIn: boolean;
  isLoading: boolean;
  isError: boolean;
  error: Error | null;
  isPending: boolean;
}

export function useAuth(): IAuthProps {
  const queryClient = useQueryClient();

  // Token refresh mutation
  const refreshMutation = useMutation({
    mutationFn: async (): Promise<MyResponse<AuthResponse>> => {
      const refreshToken = localStorage.getItem('refreshToken');
      if (typeof refreshToken !== 'string' || refreshToken === '') throw new Error('No refresh token');

      const response = await axios.post(
        `${API_URL}${endpoints.api.RefreshToken}`,
        { refreshToken }
      );

      if (response.data && typeof response.data === 'object' && 'accessToken' in response.data) {
        return {
          status: 200,
          data: response.data as AuthResponse,
          message: 'Token refreshed',
          error: []
        };
      } else {
        throw new Error('Invalid refresh token response');
      }
    },
    onSuccess: (data) => {
      if (data.data) {
        setExpiresAtLocalStorage(data.data.expiresIn);
        setIsLoggedIn(true);
        isLoggedInSignal.value = true;
        localStorage.setItem('accessToken', data.data.accessToken);
        queryClient.invalidateQueries({ queryKey: ['auth', 'session'] });
      }
    },
    retry: 3,
    retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000),
  });

  // Token validation and refresh function
  const validateAndRefreshToken = useCallback(async (): Promise<MyResponse<AuthResponse>> => {
    try {
      const accessToken = localStorage.getItem('accessToken');
      const expiresAt = localStorage.getItem('expiresAt');
      const expiresIn = localStorage.getItem('expiresIn');
      const refreshToken = localStorage.getItem('refreshToken');
      const idToken = localStorage.getItem('idToken');
      const githubUsername = localStorage.getItem('githubUsername');

      // Exit early if any of these are missing
      if (!accessToken || !refreshToken || !expiresAt || !expiresIn) {
        setIsLoggedIn(false);
        isLoggedInSignal.value = false;
        return {
          status: 401,
          message: "You are not logged in.",
          error: []
        };
      }

      // Check if token is expired or will expire soon (within 5 minutes)
      const isExpiringSoon = expiresAt ? parseInt(expiresAt) - Date.now() < 5 * 60 * 1000 : false;

      if (isExpiringSoon) {
        // Attempt to refresh the token
        const response = await refreshMutation.mutateAsync();
        if (response.data?.accessToken) {
          // Token refresh successful
          setIsLoggedIn(true);
          return {
            status: 200,
            data: {
              accessToken: response.data.accessToken,
              expiresAt: response.data.expiresAt,
              expiresIn: response.data.expiresIn,
              idToken: response.data.idToken,
              refreshToken: response.data.refreshToken,
              githubUsername: response.data.githubUsername
            },
            message: "Token refreshed",
            error: []
          };
        }
      } else if (expiresAt && parseInt(expiresAt) > Date.now()) {
        // Token is still valid
        setIsLoggedIn(true);
        return {
          status: 200,
          data: {
            accessToken: accessToken,
            expiresAt: expiresAt,
            expiresIn: expiresIn,
            idToken: idToken || '',
            refreshToken: refreshToken,
            githubUsername: githubUsername || ''
          },
          message: "Token refreshed",
          error: []
        };
      }

      // If we get here, either token refresh failed or token is expired
      setIsLoggedIn(false);
      isLoggedInSignal.value = false;
      return {
        status: 401,
        message: "Token refresh failed.",
        error: []
      };
    } catch (error) {
      if (error instanceof Error) {
        logger.error('Token validation failed:', error.message);
      } else {
        logger.error('An unexpected error occurred during token validation');
      }
      setIsLoggedIn(false);
      isLoggedInSignal.value = false;
      return {
        status: 401,
        message: "Token validation failed",
        error: []
      };
    }
  }, [refreshMutation]);

  // Session management with automatic token validation
  const { data: session, isPending } = useQuery<MyResponse<AuthResponse>>({
    queryKey: ['auth', 'session'],
    queryFn: async (): Promise<MyResponse<AuthResponse>> => {
      try {
        const isValid = await validateAndRefreshToken();
        if (!isValid) {
          throw new Error('Token validation failed');
        }

        const accessToken = localStorage.getItem('accessToken');
        if (typeof accessToken !== 'string' || accessToken === '') throw new Error('No access token');
        const config = {
          headers: {
            'AUTHORIZATION': `Bearer ${accessToken}`,
            'CONTENT_TYPE': 'application/json'
          }
        };

        const response = await axios.get(`${API_URL}${endpoints.api.GetUser}`, config);
        if (response.data && typeof response.data === 'object' && 'accessToken' in response.data) {
          return {
            status: 200,
            data: response.data as AuthResponse,
            message: 'Session valid',
            error: []
          };
        } else {
          throw new Error('Invalid session response');
        }
      } catch (error) {
        if (error instanceof Error) {
          logger.error('Session validation failed:', error.message);
        } else {
          logger.error('An unexpected error occurred during session validation');
        }
        if (typeof localStorage.getItem('accessToken') === 'string') localStorage.removeItem('accessToken');
        if (typeof localStorage.getItem('refreshToken') === 'string') localStorage.removeItem('refreshToken');
        if (typeof localStorage.getItem('expiresAt') === 'string') localStorage.removeItem('expiresAt');
        if (typeof localStorage.getItem('expiresIn') === 'string') localStorage.removeItem('expiresIn');
        if (typeof localStorage.getItem('idToken') === 'string') localStorage.removeItem('idToken');
        if (typeof localStorage.getItem('githubUsername') === 'string') localStorage.removeItem('githubUsername');
        setIsLoggedIn(false);
        isLoggedInSignal.value = false;
        setUserName(null);
        return {
          status: 401,
          data: undefined,
          message: 'Session invalid',
          error: ['Session expired or invalid']
        };
      }
    },
    enabled: typeof localStorage.getItem('accessToken') === 'string',
    retry: false,
    staleTime: 5 * 60 * 1000 // Consider session stale after 5 minutes
  });

  // Login mutation
  const loginMutation = useMutation({
    mutationFn: async (credentials: LoginCredentials): Promise<MyResponse<AuthResponse>> => {
      const response = await axios.post(
        `${API_URL}${endpoints.api.SignIn}`,
        credentials
      );
      if (response.data && typeof response.data === 'object' && 'accessToken' in response.data) {
        return {
          status: 200,
          data: response.data as AuthResponse,
          message: 'Login successful',
          error: []
        };
      } else {
        throw new Error('Invalid login response');
      }
    },
    onSuccess: (data) => {
      if (data.data && data.data.expiresIn && typeof data.data.expiresIn === 'string') {
        setExpiresAtLocalStorage(data.data.expiresIn);
        setIsLoggedIn(true);
        isLoggedInSignal.value = true;
        localStorage.setItem('accessToken', data.data.accessToken);
        localStorage.setItem('refreshToken', data.data.refreshToken);
        localStorage.setItem('idToken', data.data.idToken);

        if (typeof data.data.githubUsername === 'string') {
          localStorage.setItem('githubUsername', data.data.githubUsername);
        }
        setIsLoggedIn(true);
        setIsInLoginProcess(false);
        queryClient.invalidateQueries({ queryKey: ['auth', 'session'] });
      }
    },
    onError: () => {
      setIsLoggedIn(false);
      setIsInLoginProcess(false);
    }
  });

  const setExpiresAtLocalStorage = (expiresIn: string) => {
    const expiresAt = Date.now() + parseInt(expiresIn) * 1000;
    if (expiresIn && expiresIn !== "") localStorage.setItem('expiresIn', expiresIn);
    if (expiresAt && !Number.isNaN(expiresAt)) {
      localStorage.setItem('expiresAt', expiresAt.toString());
    }
  }

  // Signup mutation
  const signupMutation = useMutation({
    mutationFn: async (credentials: SignUpCredentials): Promise<MyResponse<unknown>> => {
      const response = await axios.post(
        `${API_URL}${endpoints.api.SignUp}`,
        credentials
      );
      if (response.data && typeof response.data === 'object') {
        return {
          status: 200,
          data: response.data,
          message: 'Signup successful',
          error: []
        };
      } else {
        throw new Error('Invalid signup response');
      }
    },
    onSuccess: () => {
      setIsInLoginProcess(false);
    },
    onError: () => {
      setIsInLoginProcess(false);
    }
  });

  // Verify email mutation
  const verifyMutation = useMutation({
    mutationFn: async ({ username, code }: { username: string; code: string }) => {
      const response = await axios.post(
        `${API_URL}${endpoints.api.ConfirmSignUp}`,
        { username, code }
      );
      if(response.data && typeof response.data === 'object') {
        return response.data as unknown;
      } else {
        throw new Error('Invalid verify response');
      }
    }
  });

  // Add a side effect to synchronize login state and validate token
  useEffect(() => {
    const syncLoginState = async () => {
      const myResponse = await validateAndRefreshToken();
      if (myResponse.status === 200) {
        isLoggedInSignal.value = true;
        setIsLoggedIn(true);
      } else {
        isLoggedInSignal.value = false;
        setIsLoggedIn(false);
      }
    };

    // Sync on initial load and when storage changes
    void syncLoginState();
    const onStorageChange = (): void => {void syncLoginState()};
    window.addEventListener('storage', onStorageChange);

    return () => {
      window.removeEventListener('storage', onStorageChange);
    };
  }, [validateAndRefreshToken]);

  if (session && typeof session === 'object' && session.data !== undefined && session.data !== null) {
    return {
      isLoggedIn: true,
      isPending,
      login: async (username: string, password: string) =>
        await loginMutation.mutateAsync({ username, password }),
      signup: async (credentials: SignUpCredentials) =>
        await signupMutation.mutateAsync(credentials),
      verify: async (username: string, code: string) =>
        await verifyMutation.mutateAsync({ username, code }),
      refreshToken: async () => await refreshMutation.mutateAsync(),
      validateAndRefreshToken,
      isLoading: loginMutation.isPending || signupMutation.isPending,
      isError: loginMutation.isError || signupMutation.isError,
      error: loginMutation.error || signupMutation.error,
    }
  } else {
    return {
      isLoggedIn: false,
      isPending,
      login: async (username: string, password: string) =>
        await loginMutation.mutateAsync({ username, password }),
      signup: async (credentials: SignUpCredentials) =>
        await signupMutation.mutateAsync(credentials),
      verify: async (username: string, code: string) =>
        await verifyMutation.mutateAsync({ username, code }),
      refreshToken: async () => await refreshMutation.mutateAsync(),
      validateAndRefreshToken,
      isLoading: loginMutation.isPending || signupMutation.isPending,
      isError: loginMutation.isError || signupMutation.isError,
      error: loginMutation.error || signupMutation.error,
    };
  }
}

import { useCallback } from 'react';

import { isInLoginProcessSignal, isInSignUpProcessSignal } from 'src/signals/appSignals';

interface IUseCharacterHandlerProps {
  setLastTypedCharacter: (value:string|null) => void;
  isInSvgMode: boolean;
  isInLoginProcess: boolean;
  writeOutputInternal: (output: string) => void;
}

export type { IUseCharacterHandlerProps };

export const useCharacterHandler = ({
  setLastTypedCharacter,
  isInSvgMode,
  writeOutputInternal,
}: IUseCharacterHandlerProps) => {

  const handleCharacter = useCallback((character: string) => {
    localStorage.setItem('currentCharacter', character);

    if (isInSvgMode) {
      setLastTypedCharacter(character);
    } else {
      setLastTypedCharacter(null);
    }

    if (isInSignUpProcessSignal.value || isInLoginProcessSignal.value) {
      writeOutputInternal('*');
    }

    return;
  }, [
    setLastTypedCharacter,
    isInSvgMode,
    writeOutputInternal,
  ]);

  return { handleCharacter };
};

// src/hooks/useCommand.ts
import type React from 'react';
import { useContext, useState, useCallback, useEffect } from 'react';

import { useComputed } from '@preact/signals-react';

import { activitySignal, appendToOutput } from 'src/signals/appSignals';
import { setCommandTime } from 'src/signals/commandLineSignals';
import { createLogger } from 'src/utils/Logger';

import { commandRegistry } from '../commands/commandRegistry';
import { CommandContext, type ICommandResponse } from '../contexts/CommandContext';
import { LogKeys } from '../types/TerminalTypes';
import { ActivityType, type OutputElement, type ParsedCommand } from '../types/Types';
import { parsedCommandToString, loadCommandHistory, saveCommandHistory } from '../utils/commandUtils';

import { useActivityMediator } from './useActivityMediator';
import { useWPMCalculator } from './useWPMCaculator';


const logger = createLogger({ prefix: 'useCommand' });

export const useCommand = () => {
    const [output, setOutput] = useState<OutputElement[]>([]);
    const [commandHistory, setCommandHistory] = useState<string[]>([]);
    const [commandHistoryIndex, setCommandHistoryIndex] = useState(-1);
    const [_commandHistoryFilter, _setCommandHistoryFilter] = useState<string | null>(null);
    const currentActivity = useComputed(() => activitySignal.value);
    const {
        handleCommandExecuted,
        checkTutorialProgress
    } = useActivityMediator();
    const wpmCalculator = useWPMCalculator();
    const context = useContext(CommandContext);

    if (context === undefined) {
        throw new Error('useCommand must be used within a CommandProvider');
    }

    // Load command history from localStorage on mount
    useEffect(() => {
        const savedHistory = loadCommandHistory();
        if (savedHistory && savedHistory.length > 0) {
            logger.debug('Loaded command history:', savedHistory);
            setCommandHistory(savedHistory);
        }
    }, []);

    const resetOutput = useCallback(() => {
        logger.debug('Resetting output');
        setOutput([]);
    }, []);

    const addToCommandHistory = useCallback((command: ParsedCommand | string) => {
        const commandString = typeof command === 'string'
            ? command
            : parsedCommandToString(command);

        logger.debug('Adding to command history:', commandString);
        setCommandHistory(prev => {
            // Don't add if it's identical to the previous command
            if (prev.length > 0 && prev[prev.length - 1] === commandString) {
                return prev;
            }
            const newHistory = [...prev, commandString].slice(-120); // Keep last 120 commands
            saveCommandHistory(newHistory); // Save to localStorage whenever history changes
            return newHistory;
        });
    }, []);

    const getCommandResponseHistory = useCallback((): string[] => {
        const keys: string[] = [];
        const history: string[] = [];
        for (let i = 0; i < localStorage.length; i++) {
            const key = localStorage.key(i);
            if (key?.startsWith(LogKeys.Command + '_')) {
                keys.push(key);
            }
        }
        keys.sort();
        for (const key of keys) {
            const historyJSON = localStorage.getItem(key);
            if (historyJSON) {
                history.push(historyJSON);
            }
        }
        logger.debug('Retrieved command response history:', history);
        return history;
    }, []);

    const createCommandRecord = useCallback((
        command: ParsedCommand,
        response: React.ReactNode,
        status: number,
        commandTime: Date,
        sensitive?: boolean
    ): OutputElement => {
        const wpms = wpmCalculator.getWPMs();
        logger.debug('Creating command record:', { command, response, status });
        return {
            command,
            response,
            status,
            wpmAverage: wpms.wpmAverage,
            characterAverages: wpms.charWpms,
            commandTime,
            sensitive
        };
    }, [wpmCalculator]);

    const processCommandOutput = useCallback((
        command: ParsedCommand,
        response: ICommandResponse,
    ): void => {
        logger.debug('Processing command output:', { command, response });
        const commandTime = new Date();
        const outputElement = createCommandRecord(
            command,
            response.message,
            response.status,
            commandTime,
            response.sensitive
        );
        appendToOutput(outputElement);
        addToCommandHistory(command);
    }, [createCommandRecord, addToCommandHistory]);

    const executeCommand = useCallback(async (parsedCommand: ParsedCommand) => {
        // Breakpoint 5: Start of command execution
        logger.debug('Executing command:', parsedCommand);
        const command = commandRegistry.getCommand(parsedCommand.command);
        if (command && context) {
            try {
                const response = await command.execute(context, parsedCommand);
                logger.debug('Command executed successfully:', response);
                processCommandOutput(parsedCommand, response);
                handleCommandExecuted(parsedCommand);
            } catch (error) {
                const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
                logger.error('Command execution failed:', error);
                processCommandOutput(parsedCommand, {
                    status: 500,
                    message: errorMessage
                });
                handleCommandExecuted(parsedCommand);
            }
        } else {
            // Breakpoint 6: Tutorial command handling
            const response = currentActivity.value === ActivityType.TUTORIAL ? `Tutorial attempt: ${parsedCommand.command}` : `Command not found: ${parsedCommand.command}`;
            logger.debug('Command not found:', { command: parsedCommand.command, activity: currentActivity.value });
            processCommandOutput(parsedCommand, {
                status: 404,
                message: response
            });
            handleCommandExecuted(parsedCommand);
        }
    }, [context, processCommandOutput, handleCommandExecuted, currentActivity]);

    const handleCommand = useCallback(async (parsedCommand: ParsedCommand) => {
        // Breakpoint 7: Start of command handling
        logger.debug('Handling command:', { parsedCommand, activity: currentActivity.value });
        setCommandTime(new Date());
        if (currentActivity.value === ActivityType.TUTORIAL) {
            // Breakpoint 8: Tutorial progress check
            logger.debug('Processing tutorial command:', parsedCommand.command);
            checkTutorialProgress(parsedCommand.command);
        }

        await executeCommand(parsedCommand);
    }, [currentActivity.value, executeCommand, checkTutorialProgress]);

    return {
        output,
        resetOutput,
        commandHistory,
        addToCommandHistory,
        getCommandResponseHistory,
        handleCommand,
        commandHistoryIndex,
        setCommandHistoryIndex,
        commandHistoryFilter: _commandHistoryFilter,
        setCommandHistoryFilter: _setCommandHistoryFilter,
        appendToOutput,
        executeCommand
    };
};

import { useEffect } from 'react';

import { useLocation, useSearchParams } from 'react-router-dom';

import { useSignal, useComputed } from '@preact/signals-react';

export function useReactiveLocation() {
  const location = useLocation();
  useSearchParams();

  const pathSignal = useSignal(location.pathname);
  const searchSignal = useSignal(location.search);
  const isInitialized = useSignal(false);

  useEffect(() => {
    if (!isInitialized.value) {
      pathSignal.value = location.pathname;
      searchSignal.value = location.search;
      isInitialized.value = true;
    } else {
      // Only update if the location has actually changed
      if (pathSignal.value !== location.pathname || searchSignal.value !== location.search) {
        pathSignal.value = location.pathname;
        searchSignal.value = location.search;
      }
    }
  }, [location, pathSignal, searchSignal, isInitialized]);


  return {
    pathname: pathSignal,
    search: searchSignal,
    isInitialized: useComputed(() => isInitialized.value)
  };
}

// useResizeCanvasAndFont.ts
import { useState, useCallback, type TouchEventHandler, useRef } from 'react';

export function useResizeCanvasAndFont() {
    const initialFontSize = parseInt(localStorage.getItem("fontSize") || '17');
    const initialCanvasHeight = parseInt(localStorage.getItem('canvas-height') || '100');
    const [fontSize, setFontSize] = useState(initialFontSize);
    const [canvasHeight, setCanvasHeight] = useState(initialCanvasHeight);
    const lastTouchDistanceRef = useRef<number | null>(null);

    const handleTouchMove = useCallback((event: TouchEvent) => {
        if (event.touches.length === 2) {
            event.preventDefault();

            const currentDistance =
                getDistanceBetweenTouches(event.touches);
            if (lastTouchDistanceRef.current && lastTouchDistanceRef.current > 0) {
                const eventTarget = event.target as HTMLElement;
                const scaleFactor = currentDistance /
                    lastTouchDistanceRef.current;
                if (eventTarget && eventTarget.nodeName ===
                    'CANVAS') {
                    setCanvasHeight(prevCanvasHeight => prevCanvasHeight * scaleFactor);
                } else {
                    setFontSize(prevFontSize => prevFontSize *
                        scaleFactor);
                    document.documentElement.style.setProperty('--rminal-font-size', `${fontSize}px`);
                }
                lastTouchDistanceRef.current = currentDistance;
            }
        }
    }, [fontSize]);

    const handleTouchStart: TouchEventHandler<HTMLElement> = (event: React.TouchEvent<HTMLElement>) => {
        setTimeout(() => {
            // this.terminalElement.focus();
        }, 500)
        if (event.touches.length === 2) {
            // event.preventDefault();
            lastTouchDistanceRef.current = getDistanceBetweenTouches(event.touches as unknown as TouchList);
        }
    }

    const handleTouchEnd: TouchEventHandler<HTMLDivElement> = () => {
        localStorage.setItem('terminalFontSize', `${fontSize}`);
        lastTouchDistanceRef.current = null;
    }

    const getDistanceBetweenTouches = (touches: TouchList): number => {
        const touch1 = touches[0];
        const touch2 = touches[1];
        return Math.sqrt(
            Math.pow(touch2.pageX - touch1.pageX, 2) +
            Math.pow(touch2.pageY - touch1.pageY, 2),
        );
    }

    // Return the state and handlers
    return {
        fontSize,
        canvasHeight,
        handleTouchMove,
        handleTouchStart,
        handleTouchEnd,
    };
}

import { useRef, useEffect } from 'react';

export function useStateLogger<T>(state: T, name: string) {
  const prevStateRef = useRef<string | null>(null);

  useEffect(() => {
    try {
      const currentState = JSON.stringify(state);

      if (prevStateRef.current !== currentState) {
        const prevState = prevStateRef.current ? JSON.parse(prevStateRef.current) : {};
        const stateChangeLog = {
          from: prevState,
          to: state
        };

        // Log error without using console.log
        const logMessage = `[${name}] State changed: ${JSON.stringify(stateChangeLog, null, 2)}`;
        if (typeof window !== 'undefined' && window.console && window.console.log) {
          window.console.log(logMessage);
        }

        prevStateRef.current = currentState;
      }
    } catch (error) {
      // Silent error handling to prevent breaking the application
      const errorMessage = error instanceof Error ? error.message : 'Unknown error in state logging';
      if (typeof window !== 'undefined' && window.console && window.console.error) {
        window.console.error(`[${name}] State logging error:`, errorMessage);
      }
    }
  }, [state, name]);
}

// hooks/useTerminal.ts
import React, { useCallback, useEffect, useRef } from 'react';

import { FitAddon } from '@xterm/addon-fit';
import { useXTerm } from 'react-xtermjs';

import { useComputed } from '@preact/signals-react';

import { TERMINAL_CONSTANTS } from 'src/constants/terminal';
import {
  isInLoginProcessSignal,
  isInSignUpProcessSignal,
  setActivity,
  setIsInLoginProcess,
  setIsInSignUpProcess,
  setTempEmail,
  setTempPassword,
  setTempUserName,
  tempEmailSignal,
  tempPasswordSignal,
  tempUserNameSignal
} from 'src/signals/appSignals';
import { addKeystroke, commandLineSignal , setCommandLine } from 'src/signals/commandLineSignals';
import { ActivityType } from 'src/types/Types';
import { parseCommand } from 'src/utils/commandUtils';
import { createLogger } from 'src/utils/Logger';

import { XtermAdapterConfig } from '../components/XtermAdapterConfig';

import { useCharacterHandler } from './useCharacterHandler';
import { useCommand } from './useCommand';
import { useWPMCalculator } from './useWPMCaculator';

const logger = createLogger({ prefix: 'useTerminal' });

export const useTerminal = () => {
  const { instance, ref: xtermRef } = useXTerm({ options: XtermAdapterConfig });
  const { handleCommand, commandHistory, commandHistoryIndex, setCommandHistoryIndex } = useCommand();
  const wpmCalculator = useWPMCalculator();
  const commandLine = useComputed(() => commandLineSignal.value);

  const [_commandLineState, _setCommandLineState] = React.useState('');

  const fitAddon = useRef(new FitAddon());

  const writeToTerminal = useCallback((data: string) => {
    logger.debug('Writing to terminal:', data);
    instance?.write(data);
  }, [instance]);

  const resetPrompt = useCallback(() => {
    if (!instance) return;
    logger.debug('Resetting prompt');
    instance.reset();
    setCommandLine('');
    _setCommandLineState('');
    instance.write(TERMINAL_CONSTANTS.PROMPT);
    instance.scrollToBottom();
  }, [instance]);

  const lastTypedCharacterRef = useRef<string | null>(null);
  const setLastTypedCharacter = (value: string | null) => {
    lastTypedCharacterRef.current = value;
  };

  const {
    handleCharacter,
  } = useCharacterHandler({
    setLastTypedCharacter,
    isInSvgMode: false,
    isInLoginProcess: isInLoginProcessSignal.value,
    writeOutputInternal: writeToTerminal,
  });

  const getCurrentCommand = useCallback(() => {
    if (!instance) return '';
    const buffer = instance.buffer.active;
    let command = '';
    for (let i = 0; i <= buffer.cursorY; i++) {
      const line = buffer.getLine(i);
      if (line) {
        command += line.translateToString(true);
      }
    }
    const promptEndIndex = command.indexOf(TERMINAL_CONSTANTS.PROMPT) + TERMINAL_CONSTANTS.PROMPT_LENGTH;
    const currentCommand = command.substring(promptEndIndex).trimStart();
    logger.debug('Getting current command:', currentCommand);
    return currentCommand;
  }, [instance]);

  const clearCurrentLine = useCallback(() => {
    if (!instance) return;
    logger.debug('Clearing current line');
    instance.write('\x1b[2K\r'); // Clear the current line
    instance.write(TERMINAL_CONSTANTS.PROMPT); // Rewrite prompt
  }, [instance]);

  const navigateHistory = useCallback((direction: 'up' | 'down') => {
    if (!instance || (commandHistory.length === 0)) return;

    let newIndex = commandHistoryIndex;

    if (direction === 'up') {
      if (newIndex === -1) {
        const currentCommand = getCurrentCommand();
        if (currentCommand) {
          setCommandLine(currentCommand);
          _setCommandLineState(currentCommand);
        }
      }
      newIndex = newIndex === -1 ? commandHistory.length - 1 : Math.max(0, newIndex - 1);
    } else {
      newIndex = newIndex === -1 ? -1 : Math.min(commandHistory.length - 1, newIndex + 1);
      if (newIndex === -1) {
        clearCurrentLine();
        const savedCommand = commandLine.value;
        if (savedCommand) {
          instance.write(savedCommand);
          setCommandLine(savedCommand);
          _setCommandLineState(savedCommand);
        }
        setCommandHistoryIndex(newIndex);
        return;
      }
    }

    clearCurrentLine();
    const historicalCommand = commandHistory[newIndex] || '';
    instance.write(historicalCommand);
    setCommandLine(historicalCommand);
    _setCommandLineState(historicalCommand);
    setCommandHistoryIndex(newIndex);
  }, [
    instance,
    commandHistory,
    commandHistoryIndex,
    getCurrentCommand,
    clearCurrentLine,
    setCommandHistoryIndex,
    commandLine,
    _setCommandLineState,
  ]);

  useEffect(() => {
    if (!instance) return;
    instance.loadAddon(fitAddon.current);
    fitAddon.current.fit();
    resetPrompt();
  }, [instance, resetPrompt]);

  useEffect(() => {
    if (!instance) return;

    const handleControlCharacters = (data: string, cursorX: number) => {
      logger.debug('Handling control character:', { data, cursorX });
      switch (data) {
        case '\x03': // Ctrl+C
          setCommandLine('');
          _setCommandLineState('');
          setActivity(ActivityType.NORMAL);
          instance?.write('^C');
          resetPrompt();
          return true;

        case '\r': // Enter key
          handleEnterKey();
          return true;

        case '\x7F': // Backspace
          handleBackspace(cursorX);
          return true;

        case '\x1b[D': // Left arrow
          if (cursorX > TERMINAL_CONSTANTS.PROMPT_LENGTH) {
            instance?.write(data);
          }
          return true;

        case '\x1b[A': // Up arrow
          navigateHistory('up');
          return true;

        case '\x1b[B': // Down arrow
          navigateHistory('down');
          return true;

        default:
          return false;
      }
    };

    const handleEnterKey = () => {
      logger.debug('Handling Enter key');
      if (isInLoginProcessSignal.value) {
        const loginCommand = parseCommand([
          'login',
          tempUserNameSignal.value,
          tempPasswordSignal.value
        ].join(' '));
        handleCommand(loginCommand);
        setIsInLoginProcess(false);
        setTempPassword('');
        setTempUserName('');
      } else if (isInSignUpProcessSignal.value) {
        const signupCommand = parseCommand([
          'signup',
          tempUserNameSignal.value,
          tempEmailSignal.value,
          tempPasswordSignal.value
        ].join(' '));
        handleCommand(signupCommand);
        setIsInSignUpProcess(false);
        setTempPassword('');
        setTempUserName('');
        setTempEmail('');
      } else {
        const currentCommand = getCurrentCommand();
        logger.debug('Processing command:', currentCommand);
        const parsedCommand = parseCommand(currentCommand === '' ? '\r' : currentCommand);
        logger.debug('Parsed command:', parsedCommand);
        instance?.write('\r\n');
        setCommandLine('');
        _setCommandLineState('');
        handleCommand(parsedCommand);
        wpmCalculator.clearKeystrokes();
      }
      setCommandHistoryIndex(-1); // Reset history index after command execution
      resetPrompt();
    };

    const handleBackspace = (cursorX: number) => {
      logger.debug('Handling backspace:', { cursorX });
      if (isInLoginProcessSignal.value || isInSignUpProcessSignal.value) {
        if (tempPasswordSignal.value.length > 0) {
          tempPasswordSignal.value = tempPasswordSignal.value.slice(0, -1);
          instance?.write('\b \b');
        }
      } else if (cursorX > TERMINAL_CONSTANTS.PROMPT_LENGTH) {
        instance?.write('\b \b');
        const newCommandLine = _commandLineState.slice(0, -1);
        setCommandLine(newCommandLine);
        _setCommandLineState(newCommandLine);
      }
    };

    const handleData = (data: string) => {
      if (!instance) return;
      logger.debug('Handling terminal data:', data);
      const cursorX = instance.buffer.active.cursorX;

      if (handleControlCharacters(data, cursorX)) {
        return;
      }

      // Handle regular character input
      if (isInLoginProcessSignal.value || isInSignUpProcessSignal.value) {
        tempPasswordSignal.value += data;
        handleCharacter(data); // This will handle masking
      } else {
        const newCommandLine = _commandLineState + data;
        instance.write(data);
        setCommandLine(newCommandLine);
        _setCommandLineState(newCommandLine);
        addKeystroke(data);
      }
      return;
    };

    const resizeHandler = () => {
      fitAddon.current.fit();
      instance.scrollToBottom();
    };
    window.addEventListener('resize', resizeHandler);

    const dataHandler = instance.onData(handleData);

    return () => {
      window.removeEventListener('resize', resizeHandler);
      dataHandler.dispose();
    };
  }, [
    instance,
    getCurrentCommand,
    resetPrompt,
    wpmCalculator,
    commandLine,
    navigateHistory,
    handleCharacter,
    _commandLineState,
    handleCommand,
    setCommandHistoryIndex,
  ]);

  return {
    xtermRef,
    writeToTerminal,
    resetPrompt,
  };
};

// src/hooks/useTutorial.ts
import { useState, useCallback, useEffect } from 'react';

import { useComputed } from '@preact/signals-react';

import {
    completedTutorialsSignal,
    getNextTutorial,
} from 'src/signals/tutorialSignals';
import { createLogger } from 'src/utils/Logger';
import { parseLocation } from 'src/utils/navigationUtils';

import { type Tutorial, Tutorials } from "../types/Types";

const logger = createLogger({ prefix: 'useTutorials' });

export const useTutorial = () => {
    const [, setCurrentTutorial] = useState<Tutorial | null>(null);
    const completedTutorials = useComputed(() => completedTutorialsSignal.value);

    // Get all completed tutorials as an array if needed
    const completedTutorialsArray = () => {
        const completed = [...completedTutorialsSignal.value];
        logger.debug('Completed tutorials:', completed);
        return completed;
    }

    const getIncompleteTutorials = useCallback((): Tutorial[] => {
        // Breakpoint 9: Getting incomplete tutorials
        const incomplete = Tutorials.filter(tut => !completedTutorialsArray().includes(tut.phrase));
        logger.debug('Incomplete tutorials:', incomplete);
        return incomplete;
    }, [])

    const getTutorialsInGroup = (groupName: string) => {
        // Breakpoint 10: Getting tutorials in group
        const result = Tutorials.filter(t => t.tutorialGroup === groupName);
        logger.debug('Tutorials in group:', { groupName, result });
        return result;
    }

    const getTutorialByPhrasekey = (phraseKey: string) => {
        // Breakpoint 11: Getting tutorial by phrase key
        const currentTutorial = Tutorials.find(t => t.phrase === phraseKey?.replace('_r', '\r'));
        logger.debug('Getting tutorial by phrase key:', {
            phraseKey,
            currentTutorial,
            normalizedKey: phraseKey?.replace('_r', '\r'),
            allTutorials: Tutorials.map(t => ({ phrase: t.phrase, prompt: t.prompt }))
        });
        return currentTutorial;
    }

    const canUnlockTutorial = (command: string): boolean => {
        // Breakpoint 12: Checking if can unlock tutorial
        const phraseKey = parseLocation().contentKey ?? '';
        const currentTutorial = getTutorialByPhrasekey(phraseKey);
        logger.debug('Checking if can unlock tutorial:', {
            command,
            phraseKey,
            currentTutorial,
            location: parseLocation(),
            completedTutorials: completedTutorialsArray()
        });

        if (!currentTutorial?.phrase) {
            logger.debug('No current tutorial found');
            return false;
        }

        // Breakpoint 13: Tutorial phrase comparison
        if (currentTutorial.phrase === command) {
            logger.debug('Tutorial unlocked:', command);
            return true;
        }
        logger.debug('Tutorial not unlocked:', {
            expected: currentTutorial.phrase,
            received: command,
            charCodesExpected: [...currentTutorial.phrase].map(c => c.charCodeAt(0)),
            charCodesReceived: [...command].map(c => c.charCodeAt(0))
        });
        return false;
    };

    const getIncompleteTutorialsInGroup = (groupName: string) => {
        // Breakpoint 14: Getting incomplete tutorials in group
        const incompleteTutorials = getIncompleteTutorials();
        const tutorialsInGroup = getTutorialsInGroup(groupName);

        const result = tutorialsInGroup
            .filter(tig => incompleteTutorials.includes(tig));
        logger.debug('Incomplete tutorials in group:', {
            groupName,
            result,
            allIncomplete: incompleteTutorials,
            groupTutorials: tutorialsInGroup
        });
        return result;
    }

    useEffect(() => {
        const nextTutorial = getNextTutorial();
        logger.debug('Setting current tutorial:', nextTutorial);
        setCurrentTutorial(nextTutorial);
    }, [completedTutorials]);

    return {
        getTutorialByPhrasekey,
        canUnlockTutorial,
        getTutorialsInGroup,
        getIncompleteTutorialsInGroup
    };
};

// src/hooks/useWPMCalculator.ts

import { useCallback } from 'react';

import { useComputed } from '@preact/signals-react';

import { keystrokesSignal, addKeystroke, clearKeystrokes } from 'src/signals/commandLineSignals';

import { type WPM, type WPMs } from '../types/Types';


export const useWPMCalculator = () => {
  const keystrokes = useComputed(() => keystrokesSignal.value);


  const getWPMs = useCallback((): WPMs => {
    if (keystrokes.value.length < 2) return { wpmAverage: 0, charWpms: [] };

    const charWpms: WPM[] = [];
    let totalWPM = 0;

    for (let i = 1; i < keystrokes.value.length; i++) {
      const duration = keystrokes.value[i].timestamp - keystrokes.value[i-1].timestamp;
      const minutes = duration / 60000; // Convert to minutes
      const cpm = 1 / minutes; // 1 character per minute.
      const wpm = cpm / 5; // five character words per minute.

      charWpms.push({
        character: keystrokes.value[i].char,
        wpm,
        durationMilliseconds: duration
      });

      totalWPM += wpm;
    }

    const wpmAverage = totalWPM / (keystrokes.value.length - 1);

    return {
      wpmAverage,
      charWpms: Object.freeze(charWpms) // Make charWpms immutable
    };
  }, [keystrokes]);

  return {
    addKeystroke,
    getWPMs, // Return the memoized value
    clearKeystrokes,
    keystrokeCount: keystrokes.value.length // Add this for debugging
  };
};

