import { createLogger, LogLevel } from './utils/Logger';

const logger = createLogger({
  prefix: 'TerminalData',
  level: LogLevel.DEBUG
});

function openDatabase() {
  const request = indexedDB.open('HandTermDatabase', 1);

  request.onupgradeneeded = function() {
    const db = request.result;
    if (!db.objectStoreNames.contains('terminalData')) {
      db.createObjectStore('terminalData', { keyPath: 'id' });
    }
  };

  request.onsuccess = function() {
    const db = request.result;
    const transaction = db.transaction(['terminalData'], 'readwrite');
    const store = transaction.objectStore('terminalData');

    const result = store.get('someKey');
    result.onsuccess = function() {
      logger.debug('Retrieved data:', result.result);
    };
  };

  const openDb = request;
  openDb.onerror = function(event: Event) {
    const target = event.target as IDBOpenDBRequest | null;
    if (target && 'error' in target) {
      logger.error('IndexedDB error:', (target).error);
    } else {
      logger.error('Unknown IndexedDB error occurred');
    }
  };

  return openDb;
}

export { openDatabase };

import { Mock } from 'vitest';

export interface MockCanvasContext {
  fillRect: Mock;
  clearRect: Mock;
  drawImage: Mock;
  beginPath: Mock;
  closePath: Mock;
  stroke: Mock;
  fill: Mock;
}

export interface CanvasMock {
  mockGetContext: Mock;
  restoreOriginalGetContext: () => void;
}

export function createMockCanvasContext(): MockCanvasContext;
export function setupCanvasMock(): CanvasMock;

import { vi } from 'vitest';

// Define an interface for the mock canvas context methods
interface MockCanvasContext {
  fillRect: ReturnType<typeof vi.fn>;
  clearRect: ReturnType<typeof vi.fn>;
  drawImage: ReturnType<typeof vi.fn>;
  beginPath: ReturnType<typeof vi.fn>;
  closePath: ReturnType<typeof vi.fn>;
  stroke: ReturnType<typeof vi.fn>;
  fill: ReturnType<typeof vi.fn>;
}

// Create a reusable canvas context mock
export const createMockCanvasContext = (): MockCanvasContext => {
  const mockContext: MockCanvasContext = {
    fillRect: vi.fn(),
    clearRect: vi.fn(),
    drawImage: vi.fn(),
    beginPath: vi.fn(),
    closePath: vi.fn(),
    stroke: vi.fn(),
    fill: vi.fn(),
  };

  return mockContext;
};

// Global mock for canvas context
export const setupCanvasMock = () => {
  const originalGetContext = HTMLCanvasElement.prototype.getContext;
  const mockGetContext = vi.fn(() => createMockCanvasContext());

  Object.defineProperty(HTMLCanvasElement.prototype, 'getContext', {
    value: mockGetContext,
    configurable: true,
    writable: true
  });

  return {
    mockGetContext,
    restoreOriginalGetContext: () => {
      // Safely restore original implementation
      if (originalGetContext) {
        Object.defineProperty(HTMLCanvasElement.prototype, 'getContext', {
          value: originalGetContext,
          configurable: true,
          writable: true
        });
      } else {
        // If no original method existed, remove the property
        const canvasProto = HTMLCanvasElement.prototype as { getContext?: unknown };
        delete canvasProto.getContext;
      }
    }
  };
